import "../styles/Footer.css";

const Footer = () => {
  return (
    <footer className="footer">
      <div className="footer-container">
        
        {/* üè™ Section 1: Store Information */}
        <div>
          <h2>üõçÔ∏è Shopwise</h2>
          <p>Your best online store for high-quality products at great prices.</p>
        </div>

        {/* üîó Section 2: Quick Links */}
        <div>
          <h3>Quick Links</h3>
          <ul className="footer-links">
            <li><Link to="/">üè† Home</Link></li>
            <li><Link to="/categories">üìÇ Categories</Link></li>
            <li><Link to="/about">‚ÑπÔ∏è About Us</Link></li>
            <li><Link to="/contact">üìû Contact Us</Link></li>
          </ul>
        </div>

        {/* üåê Section 3: Contact Information */}
        <div>
          <h3>Contact Us</h3>
          <p>üìç Address: Trade Street, Riyadh</p>
          <p>üìß Email: support@shopwise.com</p>
          <p>üìû Phone: +966 920012345</p>
          <div className="social-links">
            <a href="#">üåç Facebook</a>
            <a href="#">üê¶ Twitter</a>
            <a href="#">üì∑ Instagram</a>
          </div>
        </div>

      </div>

      <div className="footer-bottom">
        &copy; {new Date().getFullYear()} Shopwise. All rights reserved.
      </div>

    </footer>
  );
};

export default Footer;
