// AdminDashboard.jsx
import React, { useEffect, useState } from "react";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);

  const fetchWaitingUsers = async () => {
    try {
      const res = await fetch("http://localhost:7080/admin/waiting-user");
      const data = await res.json();
      setUsers(data);
    } catch (error) {
      alert("Error loading users");
    }
  };

  const acceptUser = async (id) => {
    try {
      await fetch(`http://localhost:7080/admin/waiting-user/accept/${id}`);
      alert("User accepted");
      fetchWaitingUsers();
    } catch (error) {
      alert("Error accepting user");
    }
  };

  const rejectUser = async (id) => {
    try {
      await fetch(`http://localhost:7080/admin/waiting-user/reject/${id}`);
      alert("User rejected");
      fetchWaitingUsers();
    } catch (error) {
      alert("Error rejecting user");
    }
  };

  useEffect(() => {
    fetchWaitingUsers();
  }, []);

  return (
    <div className="admin-container">
      <h2>Waiting Users</h2>
      {users.length === 0 ? (
        <p>No pending users.</p>
      ) : (
        <ul>
          {users.map((user) => (
            <li key={user.user_id}>
              <strong>{user.user_name}</strong> ({user.email}) - Role:{" "}
              {user.roleName}
              <button onClick={() => acceptUser(user.user_id)}>Accept</button>
              <button onClick={() => rejectUser(user.user_id)}>Reject</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AdminDashboard;
