import React from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";

const products = [
  { id: 1, name: "منتج 1", description: "وصف المنتج 1" },
  { id: 2, name: "منتج 2", description: "وصف المنتج 2" },
  { id: 3, name: "منتج 3", description: "وصف المنتج 3" },
];

const ProductList = () => {
  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">قائمة المنتجات</h1>
      <ul>
        {products.map((product) => (
          <li key={product.id} className="mb-2">
            <Link to={`/product/${product.id}`} className="text-blue-500 underline">
              {product.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

const ProductDetail = () => {
  const { id } = useParams();
  const product = products.find((p) => p.id === parseInt(id));
  if (!product) {
    return <div className="p-4">المنتج غير موجود</div>;
  }
  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">{product.name}</h1>
      <p>{product.description}</p>
      <Link to="/" className="text-blue-500 underline mt-4 block">العودة إلى القائمة</Link>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<ProductList />} />
        <Route path="/product/:id" element={<ProductDetail />} />
      </Routes>
    </Router>
  );
};

export default App;
