import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
    userType: 'tenant', // 'tenant' by default
    propertyOwnership: '', // فقط للمالكين
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  // دالة للتعامل مع التغيير في المدخلات
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // التحقق من صحة المدخلات
  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    // التحقق من اسم المستخدم
    if (!formData.username) {
      formErrors.username = "Username is required.";
      isValid = false;
    }

    // التحقق من البريد الإلكتروني
    const emailPattern = /^[a-zA-Z0-9._-]+@[a-zAZ0-9.-]+\.[a-zA-Z]{2,6}$/;
    if (!formData.email) {
      formErrors.email = "Email is required.";
      isValid = false;
    } else if (!emailPattern.test(formData.email)) {
      formErrors.email = "Please enter a valid email.";
      isValid = false;
    }

    // التحقق من رقم الهاتف
    if (formData.phone && !/^\d{10}$/.test(formData.phone)) {
      formErrors.phone = "Phone number should be 10 digits.";
      isValid = false;
    }

    // التحقق من كلمة المرور
    if (!formData.password) {
      formErrors.password = "Password is required.";
      isValid = false;
    } else if (formData.password.length < 6) {
      formErrors.password = "Password must be at least 6 characters.";
      isValid = false;
    }

    // التحقق من تأكيد كلمة المرور
    if (formData.password !== formData.confirmPassword) {
      formErrors.confirmPassword = "Passwords do not match.";
      isValid = false;
    }

    // إذا كان مالك، يجب رفع صورة ملكية العقار
    if (formData.userType === 'landlord' && !formData.propertyOwnership) {
      formErrors.propertyOwnership = "Property ownership certificate is required.";
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  // دالة إرسال البيانات
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      // إذا كان مالك، يتم تعيين الحالة إلى "معلق" (pending)
      const newUser = {
        username: formData.username,
        email: formData.email,
        phone: formData.phone,
        password: formData.password,
        userType: formData.userType,
        propertyOwnership: formData.propertyOwnership,
        status: formData.userType === 'landlord' ? 'pending' : 'approved', // مالك يتم تعيين حالته "معلق" (pending)
      };

      // حفظ البيانات في قاعدة البيانات (محاكاة)
      console.log('New User:', newUser);

      alert("Registration successful! Waiting for admin approval if you're a landlord.");
      navigate('/login'); // العودة إلى صفحة تسجيل الدخول بعد التسجيل
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <input 
              type="text" 
              name="username" 
              value={formData.username} 
              onChange={handleInputChange} 
              placeholder="Username" 
            />
            {errors.username && <div className="error">{errors.username}</div>}
          </div>

          <div>
            <input 
              type="email" 
              name="email" 
              value={formData.email} 
              onChange={handleInputChange} 
              placeholder="Email" 
            />
            {errors.email && <div className="error">{errors.email}</div>}
          </div>

          <div>
            <input 
              type="text" 
              name="phone" 
              value={formData.phone} 
              onChange={handleInputChange} 
              placeholder="Phone Number" 
            />
            {errors.phone && <div className="error">{errors.phone}</div>}
          </div>

          <div>
            <input 
              type="password" 
              name="password" 
              value={formData.password} 
              onChange={handleInputChange} 
              placeholder="Password" 
            />
            {errors.password && <div className="error">{errors.password}</div>}
          </div>

          <div>
            <input 
              type="password" 
              name="confirmPassword" 
              value={formData.confirmPassword} 
              onChange={handleInputChange} 
              placeholder="Confirm Password" 
            />
            {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
          </div>

          <div>
            <select 
              name="userType" 
              value={formData.userType} 
              onChange={handleInputChange}>
              <option value="tenant">Tenant</option>
              <option value="landlord">Landlord</option>
            </select>
          </div>

          {formData.userType === 'landlord' && (
            <div>
              <label>Upload Property Ownership Certificate:</label>
              <input 
                type="file" 
                name="propertyOwnership" 
                onChange={handleInputChange} 
              />
              {errors.propertyOwnership && <div className="error">{errors.propertyOwnership}</div>}
            </div>
          )}

          <button type="submit" className="btn">Register</button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
