import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { FaHeart, FaRegHeart, FaCheckCircle, FaRegCommentDots, FaWhatsapp, FaComments } from "react-icons/fa";
import { Modal, Button } from "react-bootstrap";
import "../styles/ProductDetails.css";

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [newComment, setNewComment] = useState("");
  const [comments, setComments] = useState([]);
  const [views, setViews] = useState(0);
  const [user, setUser] = useState(null); 

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        setLoading(false);
        setComments([
          { user: "John", comment: "Amazing product!" },
          { user: "Sara", comment: "Worth the price." },
        ]);
      })
      .catch((error) => {
        console.error("Error fetching product:", error);
        setLoading(false);
      });

    fetch("/check-auth", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.authenticated) {
          setUser(data.user);
        } else {
          setUser(null);
        }
      })
      .catch((error) => {
        console.error("Error checking user authentication:", error);
        setUser(null);
      });

    const viewed = localStorage.getItem(`views-${id}`);
    const currentViews = viewed ? parseInt(viewed) + 1 : 1;
    localStorage.setItem(`views-${id}`, currentViews);
    setViews(currentViews);
  }, [id]);

  const requireLogin = () => {
    alert("You must be logged in to perform this action.");
    navigate("/login");
  };

  const toggleFavorite = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setIsFavorite((prev) => !prev);
  };

  const toggleLike = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setIsLiked((prev) => !prev);
  };

  const openCommentModal = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setShowCommentModal(true);
  };

  const closeCommentModal = () => setShowCommentModal(false);

  const handleCommentSubmit = () => {
    if (newComment.trim() === "") {
      alert("Please enter a comment.");
      return;
    }
    const updatedComments = [
      ...comments,
      { user: user.name || "User", comment: newComment },
    ];
    setComments(updatedComments);
    setNewComment("");
    closeCommentModal();
  };

  if (loading) return <div className="container">Loading...</div>;
  if (!product) return <div className="container">Product not found.</div>;

  return (
    <div className="container">
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} />
      <p><strong>Price:</strong> ${product.price}</p>
      <p><strong>Description:</strong> {product.description}</p>
      <p><strong>Category:</strong> {product.category}</p>
      <p><strong>Location:</strong> Unknown Location</p>
      <p><strong>Views:</strong> {views}</p>

      <div>
        <button onClick={toggleFavorite}>
          {isFavorite ? <FaHeart color="red" /> : <FaRegHeart />}
        </button>
        <button onClick={toggleLike}>
          <FaCheckCircle />
        </button>
        <button onClick={openCommentModal}>
          <FaRegCommentDots />
        </button>
        <button onClick={() => navigate(`/chat/${id}`)}>
          <FaComments />
        </button>
        <button onClick={() => window.open(`https://wa.me/`, "_blank")}>
          <FaWhatsapp />
        </button>
      </div>

      <div>
        <h4>Comments:</h4>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.user}:</strong> {comment.comment}
            </li>
          ))}
        </ul>
      </div>

      <Modal show={showCommentModal} onHide={closeCommentModal}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write your comment here..."
            rows="4"
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeCommentModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCommentSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductDetails;
