import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from '../services/api';

const EditProperty = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    images: [],
    existingImages: []
  });
  const [newImages, setNewImages] = useState([]);
  const [previewImages, setPreviewImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitLoading, setSubmitLoading] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchProperty = async () => {
      try {
        const response = await axios.get(`/api/properties/${id}`);
        const { title, description, price, location, images } = response.data;
        
        setFormData({
          title,
          description,
          price,
          location,
          images: [],
          existingImages: images
        });
      } catch (err) {
        setMessage(err.response?.data?.message || 'Failed to load property');
      } finally {
        setLoading(false);
      }
    };

    fetchProperty();
  }, [id]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setNewImages(files);
    
    const previews = files.map(file => URL.createObjectURL(file));
    setPreviewImages([...formData.existingImages, ...previews]);
  };

  const removeImage = (index, isExisting) => {
    if (isExisting) {
      setFormData({
        ...formData,
        existingImages: formData.existingImages.filter((_, i) => i !== index)
      });
    } else {
      const updatedNewImages = [...newImages];
      updatedNewImages.splice(index, 1);
      setNewImages(updatedNewImages);
      
      const updatedPreviews = [...previewImages];
      updatedPreviews.splice(index + formData.existingImages.length, 1);
      setPreviewImages(updatedPreviews);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitLoading(true);
    setMessage('');

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('description', formData.description);
      formDataToSend.append('price', formData.price);
      formDataToSend.append('location', formData.location);
      
      formData.existingImages.forEach(image => {
        formDataToSend.append('existingImages', image);
      });
      
      newImages.forEach(image => {
        formDataToSend.append('images', image);
      });

       await axios.put(`/api/properties/${id}`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMessage('Property updated successfully!');
      setTimeout(() => {
        navigate('/landlord/properties');
      }, 1500);
    } catch (err) {
      setMessage(err.response?.data?.message || 'Failed to update property');
    } finally {
      setSubmitLoading(false);
    }
  };

  if (loading) return <div>Loading property data...</div>;

  return (
    <div className="edit-property">
      <h1>Edit Property</h1>
      
      {message && <div className={`message ${message.includes('successfully') ? 'success' : 'error'}`}>{message}</div>}
      
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-group">
          <label>Description</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="form-row">
          <div className="form-group">
            <label>Price ($/month)</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Location</label>
            <input
              type="text"
              name="location"
              value={formData.location}
              onChange={handleChange}
              required
            />
          </div>
        </div>
        
        <div className="form-group">
          <label>Images</label>
          <input
            type="file"
            multiple
            onChange={handleImageChange}
            accept="image/*"
          />
          
          <div className="image-previews">
            {formData.existingImages.map((image, index) => (
              <div key={`existing-${index}`} className="image-preview">
                <img src={`/uploads/${image}`} alt={`Existing ${index}`} />
                <button 
                  type="button" 
                  onClick={() => removeImage(index, true)}
                  className="remove-image"
                >
                  ×
                </button>
              </div>
            ))}
            
            {newImages.map((_, index) => (
              <div key={`new-${index}`} className="image-preview">
                <img src={previewImages[index + formData.existingImages.length]} alt={`New ${index}`} />
                <button 
                  type="button" 
                  onClick={() => removeImage(index, false)}
                  className="remove-image"
                >
                  ×
                </button>
              </div>
            ))}
          </div>
        </div>
        
        <button type="submit" disabled={submitLoading}>
          {submitLoading ? 'Updating...' : 'Update Property'}
        </button>
      </form>
    </div>
  );
};

export default EditProperty;
