import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; 

const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // ÿ™ÿ£ÿ´Ÿäÿ± ÿßŸÑÿ™ŸÖÿ±Ÿäÿ± ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿ∏ŸÑ ÿÆŸÅŸäŸÅ ŸÑŸÑŸÄ Navbar
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(search);
  };

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      {/* ÿ¥ÿπÿßÿ± ÿßŸÑŸÖŸàŸÇÿπ */}
      <Link to="/" className="navbar-logo">
        üõçÔ∏è Shopwise
      </Link>

      {/* ŸÖÿ±ÿ®ÿπ ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑŸÖŸÜÿ™ÿµŸÅ */}
      <form onSubmit={handleSearch} className="search-box">
        <input
          type="text"
          placeholder="üîç ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖŸÜÿ™ÿ¨..."
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit">ÿ®ÿ≠ÿ´</button>
      </form>

      {/* ÿßŸÑÿ±Ÿàÿßÿ®ÿ∑ ŸÅŸä ÿ≥ÿ∑ÿ≠ ÿßŸÑŸÖŸÉÿ™ÿ® */}
      <ul className={`navbar-links ${menuOpen ? "active" : ""}`}>
        <li><Link to="/categories">üìÇ ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™</Link></li>
        <li><Link to="/wishlist">‚ù§Ô∏è ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©</Link></li>
        <li><Link to="/cart">üõí ÿßŸÑÿ≥ŸÑÿ©</Link></li>
        <li><Link to="/profile">üë§ ÿßŸÑÿ≠ÿ≥ÿßÿ®</Link></li>
      </ul>

      {/* ÿ≤ÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ŸÅŸä ÿßŸÑÿ¨ŸàÿßŸÑ */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        ‚ò∞
      </div>
    </nav>
  );
};

export default Navbar;
