import React, { useState, useEffect } from "react";
import "../styles/AcceptRequests.css";

const AcceptRequests = () => {
  const [pendingUsers, setPendingUsers] = useState([]);

  useEffect(() => {
    const fetchPendingUsers = async () => {
      try {
        const response = await fetch("http://localhost:7080/admin/waiting-user");
        const data = await response.json();
        setPendingUsers(data);
      } catch (error) {
        alert("Error fetching pending users");
      }
    };

    fetchPendingUsers();
  }, []);

  const handleApproveUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:7080/admin/waiting-user/accept/${userId}`, {
        method: "GET",
      });

      if (response.ok) {
        alert("User approved successfully!");
        setPendingUsers(pendingUsers.filter((user) => user.user_id !== userId));
      } else {
        alert("Failed to approve user");
      }
    } catch (error) {
      alert("Error approving user");
    }
  };

  const handleRejectUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:7080/admin/waiting-user/reject/${userId}`, {
        method: "GET",
      });

      if (response.ok) {
        alert("User rejected successfully!");
        setPendingUsers(pendingUsers.filter((user) => user.user_id !== userId));
      } else {
        alert("Failed to reject user");
      }
    } catch (error) {
      alert("Error rejecting user");
    }
  };

  return (
    <div className="admin-container">
      <h2>Approve Registration Requests</h2>
      <table className="requests-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {pendingUsers.map((user) => (
            <tr key={user.user_id}>
              <td>{user.fName} {user.lName}</td>
              <td>{user.email}</td>
              <td>{user.roleName}</td>
              <td>
                <button className="approve-btn" onClick={() => handleApproveUser(user.user_id)}>Approve</button>
                <button className="reject-btn" onClick={() => handleRejectUser(user.user_id)}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AcceptRequests;
