import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaCheckCircle, FaRegCommentDots } from "react-icons/fa";
import { Modal, Button } from "react-bootstrap"; 
import "../styles/Gust.css";

const Home = () => {
  const [properties, setProperties] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [likes, setLikes] = useState([]);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [selectedPropertyId, setSelectedPropertyId] = useState(null);
  const [newComment, setNewComment] = useState("");

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        const propertiesWithAdditionalData = data.map(property => ({
          ...property,
          location: "Unknown Location",
          comments: [
            { user: "John", comment: "Nice property!" },
            { user: "Alice", comment: "Great deal." }
          ]
        }));
        setProperties(propertiesWithAdditionalData);
      })
      .catch((error) => {
        console.error("Error fetching properties:", error);
      });
  }, []);

  const toggleFavorite = (id) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.includes(id)) {
        return prevFavorites.filter((propertyId) => propertyId !== id);
      } else {
        return [...prevFavorites, id];
      }
    });
  };

  const toggleLike = (id) => {
    setLikes((prevLikes) => {
      if (prevLikes.includes(id)) {
        return prevLikes.filter((propertyId) => propertyId !== id);
      } else {
        return [...prevLikes, id];
      }
    });
  };

  const openCommentModal = (id) => {
    setSelectedPropertyId(id);
    setShowCommentModal(true);
  };

  const closeCommentModal = () => {
    setShowCommentModal(false);
  };

  const handleCommentSubmit = () => {
    if (newComment.trim() === "") {
      alert("Please enter a comment before submitting.");
      return; // لا نسمح بإرسال تعليق فارغ
    }

    // هنا نضيف التعليق إلى العقار
    const updatedProperties = properties.map((property) => {
      if (property.id === selectedPropertyId) {
        return {
          ...property,
          comments: [
            ...property.comments,
            { user: "User", comment: newComment }
          ]
        };
      }
      return property;
    });

    // تحديث الـ state مع التعليق الجديد
    setProperties(updatedProperties);
    setNewComment(""); // إعادة تعيين النص في مربع الكتابة
    closeCommentModal(); // إغلاق النافذة المنبثقة

    // يمكن إضافة الكود لإرسال التعليق إلى الخادم هنا (مثال: باستخدام fetch أو axios)
    // sendCommentToServer(selectedPropertyId, newComment);  // هذا كود افتراضي
    alert("Your comment has been submitted successfully!");
  };

  return (
    <div className="home-container">
      <h2>Available Properties</h2>
      <div className="properties-list">
        {properties.map((property) => (
          <div key={property.id} className="property-card">
            <img src={property.image} alt={property.title} className="property-image" />
            <h3>{property.title}</h3>
            <p>{property.description}</p>
            <p><strong>Price: ${property.price}</strong></p>
            <p><strong>Location: {property.location}</strong></p>

            <button
              onClick={() => toggleFavorite(property.id)}
              className={`favorite-btn ${favorites.includes(property.id) ? "favorited" : ""}`}
            >
              {favorites.includes(property.id) ? <FaHeart /> : <FaRegHeart />}
            </button>

            <button
              onClick={() => toggleLike(property.id)}
              className={`like-btn ${likes.includes(property.id) ? "liked" : ""}`}
            >
              {likes.includes(property.id) ? <FaCheckCircle /> : <FaCheckCircle style={{ opacity: 0.5 }} />}
              <span>{likes.filter(id => id === property.id).length}</span>
            </button>

            <button
              onClick={() => openCommentModal(property.id)}
              className="comment-btn"
            >
              <FaRegCommentDots />
            </button>

            <div className="comments-section">
              <h4>Comments:</h4>
              <ul>
                {property.comments.map((comment, index) => (
                  <li key={index}>
                    <strong>{comment.user}: </strong>{comment.comment}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>

      {/* Modal for adding comments */}
      <Modal show={showCommentModal} onHide={closeCommentModal}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write your comment here..."
            rows="4"
            className="comment-textarea"
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeCommentModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCommentSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Home;
