import React, { useState, useEffect } from "react";
import { FaHeart, FaRegHeart, FaCheckCircle, FaRegCommentDots, FaStar, FaRegStar } from "react-icons/fa";
import { Modal, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "../styles/Gust.css";

const GuestHome = ({ user }) => {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [favorites, setFavorites] = useState([]);
  const [likes, setLikes] = useState([]);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [selectedPropertyId, setSelectedPropertyId] = useState(null);
  const [newComment, setNewComment] = useState("");
  const [filters, setFilters] = useState({
    propertyType: "", // نوع العقار
    priceMin: "", // السعر من
    priceMax: "", // السعر إلى
    rooms: "", // عدد الغرف
    bathrooms: "", // عدد الحمامات
    amenities: [], // المزايا
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetch("https://fakestoreapi.com/products")
      .then((response) => response.json())
      .then((data) => {
        const propertiesWithExtraData = data.map((property) => ({
          ...property,
          location: "Unknown Location",
          rooms: 3,
          bathrooms: 2,
          propertyType: "Apartment", // فرضًا نوع العقار
          amenities: ["gym", "pool"],
          comments: [
            { user: "John", comment: "Nice property!" },
            { user: "Alice", comment: "Great deal." },
          ],
        }));
        setProperties(propertiesWithExtraData);
        setFilteredProperties(propertiesWithExtraData);
      })
      .catch((error) => {
        console.error("Error fetching properties:", error);
      });
  }, []);

  const requireLogin = () => {
    alert("You must be logged in to perform this action.");
    navigate("/login");
  };

  const toggleFavorite = (id) => {
    if (!user) return requireLogin();
    setFavorites((prev) =>
      prev.includes(id) ? prev.filter((pid) => pid !== id) : [...prev, id]
    );
  };

  const toggleLike = (id) => {
    if (!user) return requireLogin();
    setLikes((prev) =>
      prev.includes(id) ? prev.filter((pid) => pid !== id) : [...prev, id]
    );
  };

  const openCommentModal = (id) => {
    if (!user) return requireLogin();
    setSelectedPropertyId(id);
    setShowCommentModal(true);
  };

  const closeCommentModal = () => setShowCommentModal(false);

  const handleCommentSubmit = () => {
    if (newComment.trim() === "") {
      alert("Please enter a comment.");
      return;
    }

    const updated = properties.map((property) =>
      property.id === selectedPropertyId
        ? {
            ...property,
            comments: [...property.comments, { user: user?.name || "User", comment: newComment }],
          }
        : property
    );

    setProperties(updated);
    setNewComment("");
    closeCommentModal();
  };

  const goToDetails = (id) => {
    navigate(`/product/${id}`);
  };

  const handleFilterChange = (e) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      setFilters((prevFilters) => {
        const updatedAmenities = checked
          ? [...prevFilters.amenities, value]
          : prevFilters.amenities.filter((amenity) => amenity !== value);
        return { ...prevFilters, amenities: updatedAmenities };
      });
    } else {
      setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
    }
  };

  const filterProperties = () => {
    let filtered = properties;

    if (filters.propertyType) {
      filtered = filtered.filter((property) => property.propertyType.toLowerCase() === filters.propertyType.toLowerCase());
    }

    if (filters.priceMin) {
      filtered = filtered.filter((property) => property.price >= filters.priceMin);
    }
    if (filters.priceMax) {
      filtered = filtered.filter((property) => property.price <= filters.priceMax);
    }

    if (filters.rooms) {
      filtered = filtered.filter((property) => property.rooms >= filters.rooms);
    }

    if (filters.bathrooms) {
      filtered = filtered.filter((property) => property.bathrooms >= filters.bathrooms);
    }

    if (filters.amenities.length > 0) {
      filtered = filtered.filter((property) =>
        filters.amenities.every((amenity) => property.amenities.includes(amenity))
      );
    }

    setFilteredProperties(filtered);
  };

  useEffect(() => {
    filterProperties();
  }, [filters]);

  return (
    <div className="guest-home-container">
      <h2 className="guest-home-title">Available Properties</h2>

      {/* Filter Section */}
      <div className="filter-section">
        <div className="filter-item">
          <label>نوع العقار:</label>
          <select name="propertyType" value={filters.propertyType} onChange={handleFilterChange}>
            <option value="">اختر النوع</option>
            <option value="Apartment">شقة</option>
            <option value="Villa">فيلا</option>
          </select>
        </div>

        <div className="filter-item">
          <label>السعر من:</label>
          <input
            type="number"
            name="priceMin"
            value={filters.priceMin}
            onChange={handleFilterChange}
            placeholder="من"
          />
        </div>

        <div className="filter-item">
          <label>السعر إلى:</label>
          <input
            type="number"
            name="priceMax"
            value={filters.priceMax}
            onChange={handleFilterChange}
            placeholder="إلى"
          />
        </div>

        <div className="filter-item">
          <label>عدد الغرف:</label>
          <select name="rooms" value={filters.rooms} onChange={handleFilterChange}>
            <option value="">اختر عدد الغرف</option>
            <option value="1">1 غرفة</option>
            <option value="2">2 غرف</option>
            <option value="3">3 غرف</option>
            <option value="4">4 غرف</option>
          </select>
        </div>

        <div className="filter-item">
          <label>عدد الحمامات:</label>
          <select name="bathrooms" value={filters.bathrooms} onChange={handleFilterChange}>
            <option value="">اختر عدد الحمامات</option>
            <option value="1">1 حمام</option>
            <option value="2">2 حمام</option>
            <option value="3">3 حمام</option>
          </select>
        </div>

        <div className="filter-item">
          <label>المزايا:</label>
          <label>
            <input
              type="checkbox"
              value="gym"
              checked={filters.amenities.includes("gym")}
              onChange={handleFilterChange}
            />
            صالة رياضية
          </label>
          <label>
            <input
              type="checkbox"
              value="pool"
              checked={filters.amenities.includes("pool")}
              onChange={handleFilterChange}
            />
            مسبح
          </label>
        </div>

        <Button onClick={filterProperties}>تطبيق الفلاتر</Button>
      </div>

      {/* Properties List */}
      <div className="guest-home-list">
        {filteredProperties.map((property) => (
          <div key={property.id} className="guest-home-card" onClick={() => goToDetails(property.id)} style={{ cursor: "pointer" }}>
            <img src={property.image} alt={property.title} className="guest-home-image" />
            <h3 className="guest-home-heading">{property.title}</h3>
            <p className="guest-home-desc">{property.description}</p>
            <p><strong>Price: ${property.price}</strong></p>
            <p><strong>Location: {property.location}</strong></p>

            <div className="guest-home-actions">
              <button
                onClick={(e) => {
                  e.stopPropagation(); 
                  toggleFavorite(property.id);
                }}
                className={`guest-favorite-btn ${favorites.includes(property.id) ? "favorited" : ""}`}
              >
                {favorites.includes(property.id) ? <FaHeart /> : <FaRegHeart />}
              </button>

              <button
                onClick={(e) => {
                  e.stopPropagation();  
                  toggleLike(property.id);
                }}
                className={`guest-like-btn ${likes.includes(property.id) ? "liked" : ""}`}
              >
                <FaCheckCircle />
              </button>

              <button
                onClick={(e) => {
                  e.stopPropagation();  
                  openCommentModal(property.id);
                }}
                className="guest-comment-btn"
              >
                <FaRegCommentDots />
              </button>
            </div>

            <div className="guest-home-comments">
              <h4>Comments:</h4>
              <ul>
                {property.comments.map((comment, index) => (
                  <li key={index}>
                    <strong>{comment.user}: </strong>{comment.comment}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>

      {/* Comment Modal */}
      <Modal show={showCommentModal} onHide={closeCommentModal}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write your comment here..."
            rows="4"
            className="guest-home-textarea"
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeCommentModal}>Close</Button>
          <Button variant="primary" onClick={handleCommentSubmit}>Submit</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default GuestHome;
