import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProperty(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to load product:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p style={{ padding: "20px" }}>جاري التحميل...</p>;
  if (!property) return <p style={{ padding: "20px" }}>لم يتم العثور على المنتج.</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>{property.title}</h2>
      <img src={property.image} alt={property.title} style={{ width: "250px", marginBottom: "20px" }} />
      <p><strong>السعر:</strong> ${property.price}</p>
      <p><strong>الوصف:</strong> {property.description}</p>
      <p><strong>القسم:</strong> {property.category}</p>
      <p><strong>التقييم:</strong> {property.rating?.rate} ⭐ ({property.rating?.count} مراجعة)</p>
    </div>
  );
};

export default ProductDetails;
