import React from 'react';
import { Link } from 'react-router-dom';
import './FeaturedProperties.css';

const FeaturedProperties = ({ properties }) => {
  return (
    <div className="featured-properties-grid">
      {properties.map(property => (
        <div key={property._id} className="featured-property-card">
          <Link to={`/properties/${property._id}`}>
            <div className="property-image-container">
              <img 
                src={`/uploads/${property.images[0]}`} 
                alt={property.title}
                className="property-image"
              />
              <div className="property-badge">Featured</div>
            </div>
            <div className="property-details">
              <h3>{property.title}</h3>
              <p className="property-location">
                <span className="location-icon">üìç</span> {property.location}
              </p>
              <div className="property-meta">
                <span className="price">${property.price}/month</span>
                <span className={`status ${property.status}`}>{property.status}</span>
              </div>
            </div>
          </Link>
        </div>
      ))}
    </div>
  );
};

export default FeaturedProperties;
