// App.js
import React, { useState, useEffect } from "react";
import { Routes, Route, Navigate, Outlet, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import GuestHome from "./pages/Gust";
import ProductDetails from "./pages/ProductDetails";
import Cart from "./pages/Cart";
import Login from "./pages/Login";
import Wishlist from "./pages/Wishlist";
import Profile from "./pages/Profile";
import Checkout from "./pages/Checkout";
import Categories from "./pages/Categories";
import About from "./pages/About";
import Contact from "./pages/Contact";
import Footer from "./components/Footer";
import ScrollToTopButton from "./components/ScrollToTopButton"; // Import the ScrollToTopButton component

const ProtectedRoute = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  return user ? <Outlet /> : <Navigate to="/login" replace />;
};

const App = () => {
  const [cart, setCart] = useState([]);
  const location = useLocation();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart"));
    if (storedCart) setCart(storedCart);
  }, []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const isLoginPage = location.pathname === "/login" || location.pathname === "/register";

  return (
    <>
      {/* Render the Navbar for all pages except /login and /register */}
      {location.pathname !== "/login" && location.pathname !== "/register" && (
        <Navbar cart={cart} />
      )}

      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="/gust" element={<GuestHome />} />
        <Route path="/product/:id" element={<ProductDetails />} />

        {/* Protected Routes that require login */}
        <Route element={<ProtectedRoute />}>
          <Route path="/" element={<GuestHome />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/wishlist" element={<Wishlist />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/checkout" element={<Checkout />} />
          <Route path="/categories" element={<Categories cart={cart} setCart={setCart} />} />
        </Route>
      </Routes>

      {!isLoginPage && <Footer />}

      <ScrollToTopButton />
    </>
  );
};

export default App;
