import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css";


const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(search);
  };

  return (
    <nav className="bg-gradient-to-r from-blue-600 to-blue-800 text-white p-4 flex justify-between items-center shadow-lg sticky top-0 z-50">
      {/* شعار الموقع */}
      <Link to="/" className="text-2xl font-bold hover:text-yellow-400 transition">
        🛍️ Shopwise
      </Link>

      {/* مربع البحث */}
      <form onSubmit={handleSearch} className="hidden md:flex items-center bg-white rounded-md overflow-hidden">
        <input
          type="text"
          placeholder="🔍 ابحث عن منتج..."
          className="p-2 text-black outline-none w-64"
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit" className="bg-yellow-400 px-4 py-2 hover:bg-yellow-500 transition">
          بحث
        </button>
      </form>

      {/* الروابط */}
      <div className="hidden md:flex gap-6">
        <Link to="/categories" className="hover:text-yellow-400 transition">📂 التصنيفات</Link>
        <Link to="/wishlist" className="hover:text-yellow-400 transition">❤️ المفضلة</Link>
        <Link to="/cart" className="hover:text-yellow-400 transition">🛒 السلة</Link>
        <Link to="/profile" className="hover:text-yellow-400 transition">👤 الحساب</Link>
      </div>

      {/* زر القائمة في الجوال */}
      <button className="md:hidden text-2xl" onClick={() => setMenuOpen(!menuOpen)}>
        ☰
      </button>

      {/* القائمة المنسدلة للجوال */}
      {menuOpen && (
        <div className="absolute top-16 right-0 w-2/3 bg-blue-700 text-white flex flex-col gap-4 p-4 rounded-lg shadow-lg md:hidden">
          <Link to="/" onClick={() => setMenuOpen(false)}>🏠 الرئيسية</Link>
          <Link to="/categories" onClick={() => setMenuOpen(false)}>📂 التصنيفات</Link>
          <Link to="/wishlist" onClick={() => setMenuOpen(false)}>❤️ المفضلة</Link>
          <Link to="/cart" onClick={() => setMenuOpen(false)}>🛒 السلة</Link>
          <Link to="/profile" onClick={() => setMenuOpen(false)}>👤 الحساب</Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
