import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // لاستخدام المعاملات في URL

const ProductDetails = () => {
  const { id } = useParams(); // أخذ الـ id من URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // جلب البيانات باستخدام الـ id
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setProduct(data);
      })
      .catch((error) => console.error("Error fetching product data:", error));
  }, [id]);

  if (!product) {
    return <div>Loading...</div>; // عرض رسالة تحميل عند انتظار البيانات
  }

  return (
    <div className="product-details">
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} />
      <p>{product.description}</p>
      <p><strong>Price: ${product.price}</strong></p>
      <p><strong>Category: {product.category}</strong></p>
    </div>
  );
};

export default ProductDetails;
