import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css";

const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [showSearch, setShowSearch] = useState(false);

  // ุชุฃุซูุฑ ุงูุชูุฑูุฑ ูุชุบููุฑ ููู ุงูุดุฑูุท ุงูุนููู
  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 50);
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // ูุนุงูุฌุฉ ุงูุจุญุซ
  const handleSearch = (e) => {
    e.preventDefault();
    if (search.trim() !== "") onSearch(search);
  };

  // ุฅุบูุงู ุงููุงุฆูุฉ ุนูุฏ ุงูููุฑ ุนูู ุฑุงุจุท ูู ุงูุฌูุงู
  const handleLinkClick = () => setMenuOpen(false);

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      {/* ุดุนุงุฑ ุงููููุน */}
      <Link to="/" className="navbar-logo">
        ๐๏ธ <span>Shopwise</span>
      </Link>

      {/* ุฒุฑ ุงูุจุญุซ ููุฌูุงู */}
      
      {/* ุตูุฏูู ุงูุจุญุซ */}
      <form onSubmit={handleSearch} className={`search-box ${showSearch ? "show" : ""}`}>
        <input
          type="text"
          placeholder="๐ Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit">๐</button>
        {search && <button type="button" onClick={() => setSearch("")} className="clear-btn">โ</button>}
      </form>

      <ul className={`navbar-links ${menuOpen ? "active" : ""}`}>
        <li><Link to="/categories" onClick={handleLinkClick}>๐ Categories</Link></li>
        <li><Link to="/contact" onClick={handleLinkClick}>๐ Contact Us</Link></li>
        <li><Link to="/cart" onClick={handleLinkClick}>๐ Cart</Link></li>
        <li><Link to="/profile" onClick={handleLinkClick}>๐ค Profile</Link></li>
        <li><Link to="/login" className="sign-in-btn" onClick={handleLinkClick}>๐ Sign in</Link></li>
      </ul>

      {/* ุฒุฑ ุงููุงุฆูุฉ ูู ุงูุฌูุงู */}
      <div
        className="menu-icon"
        onClick={() => {
          setMenuOpen(!menuOpen);
          setShowSearch(false); // ุฅุบูุงู ุงูุจุญุซ ุนูุฏ ูุชุญ ุงููุงุฆูุฉ
        }}
      >
        {menuOpen ? "โ" : "โฐ"}
      </div>
    </nav>
  );
};

export default Navbar;
