import { createSlice } from "@reduxjs/toolkit";

// استرجاع المستخدم المحفوظ في localStorage عند تحميل التطبيق
const storedUser = JSON.parse(localStorage.getItem("user"));

export const authSlice = createSlice({
  name: "auth",
  initialState: { user: storedUser || null }, // تحميل المستخدم إذا كان مسجلاً
  reducers: {
    login: (state, action) => {
      state.user = action.payload;
      localStorage.setItem("user", JSON.stringify(action.payload)); // حفظ في localStorage
    },
    logout: (state) => {
      state.user = null;
      localStorage.removeItem("user"); // إزالة من localStorage
    },
  },
});

export const { login, logout } = authSlice.actions;
export default authSlice.reducer;
