import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ProductDetails = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [cart, setCart] = useState([]);

  // Fetch product data from API
  useEffect(() => {
    axios.get(`https://fakestoreapi.com/products/${id}`)
      .then(response => setProduct(response.data))
      .catch(error => console.log(error));
  }, [id]);

  // Retrieve cart data from Local Storage when the page loads
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  // Function to add product to the cart
  const addToCart = () => {
    if (!product) return;

    const updatedCart = [...cart, product]; // Add product to cart
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // Update Local Storage

    alert("Product added to cart ðŸ›’");
  };

  if (!product) return <p className="text-center">Loading product details...</p>;

  return (
    <div className="container mx-auto p-6 flex flex-col md:flex-row gap-6">
      <img src={product.image} alt={product.title} className="w-full md:w-1/3 rounded-lg shadow-lg" />

      <div className="md:w-2/3">
        <h1 className="text-3xl font-bold">{product.title}</h1>
        <p className="text-gray-600 mt-2">{product.description}</p>
        <p className="text-2xl font-semibold mt-4 text-green-500">${product.price}</p>

        {/* Add to Cart Button */}
        <button 
          className="bg-blue-600 text-white px-6 py-2 rounded mt-4 hover:bg-blue-700 transition"
          onClick={addToCart}
        >
          Add to Cart ðŸ›’
        </button>
      </div>

      <div className="hidden md:block w-1/3">
        <img src="https://source.unsplash.com/400x600/?sale,discount" alt="Offer Banner" className="rounded-lg shadow-lg"/>
      </div>
    </div>
  );
};

export default ProductDetails;
