import { useContext, useState, useEffect } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import axios from '../services/api';

const TenantRoute = () => {
  const { user, loading: authLoading } = useContext(AuthContext);
  const [isValidTenant, setIsValidTenant] = useState(null);
  const [verificationLoading, setVerificationLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const verifyTenant = async () => {
      if (!user) {
        setVerificationLoading(false);
        return;
      }

      try {
        setVerificationLoading(true);
        const response = await axios.get('/tenant/verify'); 
        setIsValidTenant(response.data.isValid);
      } catch (error) {
        setError(error.response?.data?.message || 'Tenant verification failed');
        setIsValidTenant(false);
      } finally {
        setVerificationLoading(false);
      }
    };

    verifyTenant();
  }, [user]);

  if (authLoading || verificationLoading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="text-center">
          <div className="spinner border-4 border-blue-500 border-t-transparent rounded-full w-12 h-12 animate-spin mx-auto"></div>
          <p className="mt-4 text-gray-600">
            {verificationLoading ? 'Verifying your tenant status...' : 'Loading...'}
          </p>
        </div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" state={{ from: 'tenant-route' }} />;
  }

  if (error) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded max-w-md">
          <p>{error}</p>
          <p className="mt-2">You will be redirected shortly...</p>
        </div>
      </div>
    );
  }

  if (isValidTenant === false) {
    return <Navigate to="/" />;
  }

  return <Outlet />;
};

export default TenantRoute;
