import React from "react";
import { Link } from "react-router-dom";

const Hero = () => {
  return (
    <div className="relative">
      <img 
        src="https://source.unsplash.com/1600x600/?shopping,ecommerce" 
        alt="Shop Banner" 
        className="w-full h-64 md:h-96 object-cover"
      />
      <div className="absolute inset-0 bg-black bg-opacity-50 flex flex-col items-center justify-center text-white text-center px-4">
        <h1 className="text-3xl md:text-5xl font-bold">تسوق أفضل المنتجات بأفضل الأسعار!</h1>
        <Link to="/categories" className="mt-4 bg-yellow-500 px-6 py-2 rounded text-lg">
        </Link>
      </div>
    </div>
  );
};

export default Hero;
