import React, { useState } from "react";
import CartItem from "../components/CartItem";
import "../styles/Cart.css";


const Cart = () => {
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      title: "ÿ≥ÿßÿπÿ© ÿ∞ŸÉŸäÿ©",
      price: 50,
      quantity: 1,
      image: "https://source.unsplash.com/100x100/?watch",
    },
    {
      id: 2,
      title: "Ÿáÿßÿ™ŸÅ ÿ∞ŸÉŸä",
      price: 300,
      quantity: 2,
      image: "https://source.unsplash.com/100x100/?smartphone",
    },
  ]);

  const handleUpdateQuantity = (id, newQuantity) => {
    if (newQuantity < 1) return;
    setCartItems(cartItems.map(item => 
      item.id === id ? { ...item, quantity: newQuantity } : item
    ));
  };

  const handleRemoveItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  // ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä
  const totalPrice = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">üõí ÿ≥ŸÑÿ© ÿßŸÑŸÖÿ¥ÿ™ÿ±Ÿäÿßÿ™</h1>

      {cartItems.length === 0 ? (
        <p className="text-gray-500 text-center">ÿßŸÑÿ≥ŸÑÿ© ŸÅÿßÿ±ÿ∫ÿ© üòû</p>
      ) : (
        <div>
          {cartItems.map(item => (
            <CartItem key={item.id} item={item} 
              onUpdateQuantity={handleUpdateQuantity} 
              onRemove={handleRemoveItem} />
          ))}

          <div className="text-right mt-6">
            <h2 className="text-xl font-bold">üí∞ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: ${totalPrice.toFixed(2)}</h2>
            <button className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
              ÿ•ÿ™ŸÖÿßŸÖ ÿßŸÑÿ¥ÿ±ÿßÿ° ‚úÖ
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
