import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import { useContext } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: 'tenant', 
    address: ''
  });
  
  const [message, setMessage] = useState('');
  const navigate = useNavigate();
  const { register } = useContext(AuthContext);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await register(formData);
      
      if (formData.role === 'tenant') {
        navigate('/login');
        setMessage('Registration successful! Please login.');
      } else if (formData.role === 'landlord') {
        setMessage('Your registration is pending admin approval. You will be notified once approved.');
      }
    } catch (error) {
      setMessage(error.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>
        
        <div className="form-group">
          <label>Email</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>
        
        <div className="form-group">
          <label>Password</label>
          <input type="password" name="password" value={formData.password} onChange={handleChange} required />
        </div>
        
        <div className="form-group">
          <label>Role</label>
          <select name="role" value={formData.role} onChange={handleChange}>
            <option value="tenant">Tenant</option>
            <option value="landlord">Landlord</option>
          </select>
        </div>
        
  

        <div className="guest-section">
        <p>Just want to browse? Continue as guest</p>
        <button onClick={enterAsGuest} className="guest-btn">
          Enter as Guest
        </button>
      </div>
        
        <button type="submit">Register</button>
      </form>
      
      {message && <div className="message">{message}</div>}
      
      <p>Already have an account? <a href="/login">Login</a></p>
    </div>
  );
};

export default Register;
