import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { FaHeart, FaRegHeart, FaCheckCircle, FaRegCommentDots, FaWhatsapp, FaComments } from "react-icons/fa";
import { Modal, Button } from "react-bootstrap";
import "../styles/productDetailsmodule.css";



const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [newComment, setNewComment] = useState("");
  const [comments, setComments] = useState([]);
  const [views, setViews] = useState(0);
  const [user, setUser] = useState(null); 

  useEffect(() => {
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        setLoading(false);
        setComments([
          { user: "John", comment: "Amazing product!" },
          { user: "Sara", comment: "Worth the price." },
        ]);
      })
      .catch((error) => {
        console.error("Error fetching product:", error);
        setLoading(false);
      });

    fetch("/check-auth", {
      method: "POST",
      headers: {
        "Authorization": `Bearer ${localStorage.getItem("token")}`, 
      },
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.authenticated) {
          setUser(data.user);
        } else {
          setUser(null);
        }
      })
      .catch((error) => {
        console.error("Error checking user authentication:", error);
        setUser(null);
      });

    const viewed = localStorage.getItem(`views-${id}`);
    const currentViews = viewed ? parseInt(viewed) + 1 : 1;
    localStorage.setItem(`views-${id}`, currentViews);
    setViews(currentViews);
  }, [id]);

  const requireLogin = () => {
    alert("You must be logged in to perform this action.");
    navigate("/login");
  };

  const toggleFavorite = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setIsFavorite((prev) => !prev);
  };

  const toggleLike = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setIsLiked((prev) => !prev);
  };

  const openCommentModal = () => {
    if (!user) {
      requireLogin();
      return;
    }
    setShowCommentModal(true);
  };

  const closeCommentModal = () => setShowCommentModal(false);

  const handleCommentSubmit = () => {
    if (newComment.trim() === "") {
      alert("Please enter a comment.");
      return;
    }
    const updatedComments = [
      ...comments,
      { user: user.name || "User", comment: newComment },
    ];
    setComments(updatedComments);
    setNewComment("");
    closeCommentModal();
  };

  if (loading) return <div style={{ padding: "20px" }}>Loading...</div>;
  if (!product) return <div style={{ padding: "20px" }}>Product not found.</div>;

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>{product.title}</h2>
      <img src={product.image} alt={product.title} style={{ width: "100%", maxWidth: "300px" }} />
      <p><strong>Price:</strong> ${product.price}</p>
      <p><strong>Description:</strong> {product.description}</p>
      <p><strong>Category:</strong> {product.category}</p>
      <p><strong>Location:</strong> Unknown Location</p>
      <p><strong>Views:</strong> {views}</p>

      <div>
        <button onClick={toggleFavorite} style={{ margin: "5px" }}>
          {isFavorite ? <FaHeart color="red" /> : <FaRegHeart />}
        </button>
        <button onClick={toggleLike} style={{ margin: "5px" }}>
          <FaCheckCircle />
        </button>
        <button onClick={openCommentModal} style={{ margin: "5px" }}>
          <FaRegCommentDots />
        </button>
        <button onClick={() => navigate(`/chat/${id}`)} style={{ margin: "5px" }}>
          <FaComments />
        </button>
        <button onClick={() => window.open(`https://wa.me/`, "_blank")} style={{ margin: "5px" }}>
          <FaWhatsapp />
        </button>
      </div>

      <div>
        <h4>Comments:</h4>
        <ul>
          {comments.map((comment, index) => (
            <li key={index}>
              <strong>{comment.user}:</strong> {comment.comment}
            </li>
          ))}
        </ul>
      </div>

      <Modal show={showCommentModal} onHide={closeCommentModal}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write your comment here..."
            rows="4"
            style={{ width: "100%" }}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeCommentModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCommentSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductDetails;
