import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/.css";

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = () => {
    let users = JSON.parse(localStorage.getItem("users")) || [];

    if (isLogin) {
      const existingUser = users.find((user) => user.email === email && user.password === password);
      if (existingUser) {
        localStorage.setItem("user", JSON.stringify(existingUser));
        navigate("/");
      } else {
        alert("Invalid email or password!");
      }
    } else {
      if (users.find((user) => user.email === email)) {
        alert("This email is already registered!");
        return;
      }
      if (password !== confirmPassword) {
        alert("Passwords do not match!");
        return;
      }

      const newUser = { email, password };
      users.push(newUser);
      localStorage.setItem("users", JSON.stringify(users));

      alert("Account created successfully! Please log in.");
      setIsLogin(true);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <div className="auth-toggle">
          <button className={`auth-button ${isLogin ? "active" : ""}`} onClick={() => setIsLogin(true)}>Login</button>
          <button className={`auth-button ${!isLogin ? "active" : ""}`} onClick={() => setIsLogin(false)}>Register</button>
        </div>

        <h2 className="auth-title">{isLogin ? "Login" : "Register"}</h2>

        <input type="email" placeholder="Email" className="auth-input" value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="password" placeholder="Password" className="auth-input" value={password} onChange={(e) => setPassword(e.target.value)} />
        {!isLogin && (
          <input type="password" placeholder="Confirm Password" className="auth-input" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        )}
        
        <button onClick={handleSubmit} className="auth-submit">{isLogin ? "Login" : "Register"}</button>
      </div>
    </div>
  );
};

export default Auth;
