import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; 

const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Add shadow effect when scrolling
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    onSearch(search);
  };

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      {/* Logo */}
      <Link to="/" className="navbar-logo">
        üõçÔ∏è Shopwise
      </Link>

      {/* Search Box */}
      <form onSubmit={handleSearch} className="search-box">
        <input
          type="text"
          placeholder="üîç Search for products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>

      {/* Navigation Links */}
      <ul className={`navbar-links ${menuOpen ? "active" : ""}`}>
        <li><Link to="/categories">üìÇ Categories</Link></li>
        <li><Link to="/contact">üìû Contact Us</Link></li>
        <li><Link to="/cart">üõí Cart</Link></li>
        <li><Link to="/profile">üë§ Profile</Link></li>
        <li><Link to="/signin">üîë first time </Link></li>

      </ul>

      {/* Mobile Menu Button */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? "‚úñ" : "‚ò∞"}
      </div>
    </nav>
  );
};

export default Navbar;
