import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { FaHeart, FaRegHeart, FaCheckCircle, FaRegCommentDots, FaWhatsapp, FaComments } from "react-icons/fa";
import { Modal, Button } from "react-bootstrap";

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [showCommentModal, setShowCommentModal] = useState(false);
  const [newComment, setNewComment] = useState("");
  const [comments, setComments] = useState([]);
  const [views, setViews] = useState(0);

  useEffect(() => {
    // بدلاً من fakestoreapi.com في المستقبل يمكن ربط هذا الرابط بـ backend الفعلي
    fetch(`https://fakestoreapi.com/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setProduct(data);
        setLoading(false);
        setComments([
          { user: "John", comment: "Amazing product!" },
          { user: "Sara", comment: "Worth the price." },
        ]);
      })
      .catch((error) => {
        console.error("Error fetching product:", error);
        setLoading(false);
      });

    // زيادة عدد المشاهدات في الوقت الحالي
    const viewed = localStorage.getItem(`views-${id}`);
    const currentViews = viewed ? parseInt(viewed) + 1 : 1;
    localStorage.setItem(`views-${id}`, currentViews);
    setViews(currentViews);
  }, [id]);

  // التبديل بين المفضلة
  const toggleFavorite = () => {
    setIsFavorite(!isFavorite);
    updateBackend({ isFavorite: !isFavorite }); // إرسال التحديث إلى الباكند
  };

  // التبديل بين اللايك
  const toggleLike = () => {
    setIsLiked(!isLiked);
    updateBackend({ isLiked: !isLiked }); // إرسال التحديث إلى الباكند
  };

  // فتح نافذة التعليقات
  const openCommentModal = () => setShowCommentModal(true);
  const closeCommentModal = () => setShowCommentModal(false);

  // إرسال التعليق إلى الباكند
  const handleCommentSubmit = () => {
    if (newComment.trim() === "") return;
    const newCommentData = { user: "You", comment: newComment };
    setComments((prev) => [...prev, newCommentData]);
    setNewComment("");
    closeCommentModal();
    // إرسال التعليق إلى الباكند
    updateBackend({ newComment: newCommentData });
  };

  // دالة للتواصل مع الـ backend
  const updateBackend = async (data) => {
    try {
      const response = await fetch(`https://fakestoreapi.com/products/${id}`, {
        method: "PUT", // أو POST حسب الحاجة
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });
      const updatedProduct = await response.json();
      console.log("Product updated successfully", updatedProduct);
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  if (loading) return <div style={{ padding: "20px" }}>Loading...</div>;
  if (!product) return <div style={{ padding: "20px" }}>Product not found.</div>;

  return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "0 auto" }}>
      <h2>{product.title}</h2>
      <img
        src={product.image}
        alt={product.title}
        style={{ width: "100%", maxWidth: "300px", marginBottom: "20px" }}
      />

      <p><strong>Price:</strong> ${product.price}</p>
      <p><strong>Description:</strong> {product.description}</p>
      <p><strong>Category:</strong> {product.category}</p>
      <p><strong>Location:</strong> Unknown Location</p>
      <p><strong>Views:</strong> {views}</p>

      <div style={{ display: "flex", gap: "15px", marginTop: "15px" }}>
        <button onClick={toggleFavorite} style={{ background: "none", border: "none", fontSize: "20px", color: isFavorite ? "red" : "gray" }}>
          {isFavorite ? <FaHeart /> : <FaRegHeart />}
        </button>

        <button onClick={toggleLike} style={{ background: "none", border: "none", fontSize: "20px", color: isLiked ? "green" : "gray" }}>
          <FaCheckCircle />
        </button>

        <button onClick={openCommentModal} style={{ background: "none", border: "none", fontSize: "20px", color: "gray" }}>
          <FaRegCommentDots />
        </button>

        <a
          href="https://wa.me/1234567890"
          target="_blank"
          rel="noopener noreferrer"
          style={{ fontSize: "20px", color: "#25D366", textDecoration: "none" }}
        >
          <FaWhatsapp />
        </a>

        <button
          onClick={() => navigate("/chat")}
          style={{ background: "none", border: "none", fontSize: "20px", color: "#007bff" }}
        >
          <FaComments />
        </button>
      </div>

      <div style={{ marginTop: "30px" }}>
        <h4>Comments:</h4>
        <ul>
          {comments.map((comment, idx) => (
            <li key={idx}>
              <strong>{comment.user}:</strong> {comment.comment}
            </li>
          ))}
        </ul>
      </div>

      <Modal show={showCommentModal} onHide={closeCommentModal}>
        <Modal.Header closeButton>
          <Modal.Title>Leave a Comment</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <textarea
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
            placeholder="Write your comment here..."
            rows="4"
            style={{ width: "100%" }}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeCommentModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleCommentSubmit}>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ProductDetails;
