import { useContext } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import AuthContext from '../../context/AuthContext';
import axios from '../services/api';

const TenantRoute = () => {
  const { user, loading } = useContext(AuthContext);
  const [isValidTenant, setIsValidTenant] = useState(null);

  useEffect(() => {
    const verifyTenant = async () => {
      if (user) {
        try {
          const response = await axios.get('/api/tenant/verify', {
            headers: {
              Authorization: `Bearer ${user.token}`
            }
          });
          setIsValidTenant(response.data.isValid);
        } catch (error) {
          setIsValidTenant(false);
        }
      }
    };

    verifyTenant();
  }, [user]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (isValidTenant === false) {
    return <Navigate to="/" />;
  }

  if (isValidTenant === null) {
    return <div>Verifying your account...</div>;
  }

  return <Outlet />;
};

export default TenantRoute;
