import { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
import AuthContext from '../context/AuthContext';
import { useContext } from 'react';

const Messages = () => {
  const [conversations, setConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const { user } = useContext(AuthContext);
  const socket = useRef(null);

  // useEffect(() => {
  //   socket.current = io('http://localhost:5000'); 
  //   socket.current.emit('join', user._id);

  //   return () => {
  //     socket.current.disconnect();
  //   };
  // }, [user]);

  useEffect(() => {
    socket.current?.on('newMessage', (msg) => {
      if (msg.conversationId === selectedConversation?._id) {
        setMessages((prev) => [...prev, msg]);
      }
    });
  }, [selectedConversation]);

  useEffect(() => {
    const fetchConversations = async () => {
      try {
        const res = await axios.get('/api/messages/conversations');
        setConversations(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchConversations();
  }, []);

  useEffect(() => {
    if (!selectedConversation) return;

    const fetchMessages = async () => {
      try {
        const res = await axios.get(`/api/messages/${selectedConversation._id}`);
        setMessages(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchMessages();
  }, [selectedConversation]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    try {
      const res = await axios.post('/api/messages/send', {
        conversationId: selectedConversation._id,
        text: newMessage
      });

      socket.current.emit('sendMessage', res.data);

      setMessages([...messages, res.data]);
      setNewMessage('');
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div>Loading messages...</div>;

  return (
    <div className="messages-page">
      <div className="conversation-list">
        <h2>Conversations</h2>
        {conversations.length > 0 ? (
          <ul>
            {conversations.map(conv => (
              <li
                key={conv._id}
                onClick={() => setSelectedConversation(conv)}
                className={selectedConversation?._id === conv._id ? 'active' : ''}
              >
                <p>{conv.participants.find(p => p._id !== user._id).name}</p>
                <p className="preview">{conv.lastMessage?.text || 'No messages yet'}</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>No conversations yet</p>
        )}
      </div>

      <div className="message-view">
        {selectedConversation ? (
          <>
            <div className="message-header">
              <h2>{selectedConversation.participants.find(p => p._id !== user._id).name}</h2>
            </div>

            <div className="messages-container">
              {messages.length > 0 ? (
                messages.map(msg => (
                  <div
                    key={msg._id}
                    className={`message ${msg.sender._id === user._id ? 'sent' : 'received'}`}
                  >
                    <p>{msg.text}</p>
                    <span className="time">{new Date(msg.createdAt).toLocaleTimeString()}</span>
                  </div>
                ))
              ) : (
                <p>No messages in this conversation</p>
              )}
            </div>

            <form className="message-input" onSubmit={handleSendMessage}>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
              />
              <button type="submit">Send</button>
            </form>
          </>
        ) : (
          <div className="select-conversation">
            <p>Select a conversation to view messages</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Messages;
