// Login.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    username: "",
    confirmPassword: "",
    userType: "tenant",
  });
  const [errors, setErrors] = useState({});
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const validateForm = () => {
    const newErrors = {};
    let valid = true;

    if (!formData.email) {
      newErrors.email = "Email is required";
      valid = false;
    }

    if (!formData.password) {
      newErrors.password = "Password is required";
      valid = false;
    }

    if (!isLogin && formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
      valid = false;
    }

    if (!isLogin && !formData.username) {
      newErrors.username = "Username is required";
      valid = false;
    }

    setErrors(newErrors);
    return valid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      if (isLogin) {
        if (formData.userType === "guest") {
          alert("You are logged in as a Guest.");
          navigate("/Gust");
          return;
        }

        const response = await fetch("http://localhost:7080/user/login", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            email: formData.email,
            password: formData.password,
          }),
        });

        const data = await response.json();
        if (response.ok) {
          alert("Login successful!");
          if (data.roleName === "admin") {
            navigate("/admin-dashboard");
          } else if (data.roleName === "landlord") {
            navigate("/landlord-dashboard");
          } else {
            navigate("/tenant-dashboard");
          }
        } else {
          alert(data.message || "Login failed");
        }
      } else {
        const response = await fetch("http://localhost:7080/user/register", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            user_name: formData.username,
            email: formData.email,
            password: formData.password,
            roleName: formData.userType,
          }),
        });

        const data = await response.json();
        if (response.ok) {
          alert("Registration successful!");
          navigate("/"); 
        } else {
          alert(data.message || "Registration failed");
        }
      }
    } catch (err) {
      alert("Error: " + err.message);
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h2>{isLogin ? "Login" : "Register"}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Email"
          />
          {errors.email && <div className="error">{errors.email}</div>}

          {!isLogin && (
            <>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="Username"
              />
              {errors.username && <div className="error">{errors.username}</div>}
            </>
          )}

          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            placeholder="Password"
          />
          {errors.password && <div className="error">{errors.password}</div>}

          {!isLogin && (
            <>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                placeholder="Confirm Password"
              />
              {errors.confirmPassword && (
                <div className="error">{errors.confirmPassword}</div>
              )}

              <select
                name="userType"
                value={formData.userType}
                onChange={handleInputChange}
              >
                <option value="tenant">Tenant</option>
                <option value="landlord">Landlord</option>
              </select>
            </>
          )}

          <button type="submit" className="btn">
            {isLogin ? "Login" : "Register"}
          </button>
        </form>

        <p>
          {isLogin ? "Don't have an account? " : "Already have an account? "}
          <span onClick={() => setIsLogin(!isLogin)} className="toggle-link">
            {isLogin ? "Register" : "Login"}
          </span>
        </p>

        <p>
          <span onClick={() => navigate("/Gust")} className="toggle-link">
            Login as Guest
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
