import React from "react";
import "../styles/Cart.css";
import { useNavigate } from "react-router-dom";

const Cart = ({ cart = [], setCart }) => {  
  const navigate = useNavigate();

  const removeFromCart = (productId) => {
    setCart(cart.filter((item) => item.id !== productId));
  };

  const updateQuantity = (productId, amount) => {
    setCart(
      cart.map((item) =>
        item.id === productId ? { ...item, quantity: Math.max(1, item.quantity + amount) } : item
      )
    );
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cart.length > 0 ? ( 
        <div className="cart-items">
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={item.image} alt={item.title} className="cart-item-image" />
              <div className="cart-item-details">
                <h3>{item.title}</h3>
                <p>${(item.price * item.quantity).toFixed(2)}</p> 
                <div className="cart-item-actions">
                  <button onClick={() => updateQuantity(item.id, -1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQuantity(item.id, 1)}>+</button>
                  <button onClick={() => removeFromCart(item.id)} className="remove-button">Remove</button>
                </div>
              </div>
            </div>
          ))}
          <h3 className="cart-total">Total: ${calculateTotal()}</h3>
        </div>
      ) : (
        <p>Your cart is empty.</p>
      )}
      <button className="back-button" onClick={() => navigate("/")}>Continue Shopping</button>
      {cart.length > 0 && (
        <button className="checkout-button" onClick={() => navigate("/checkout", { state: { cart, total: calculateTotal() } })}>
          Proceed to Checkout
        </button>
      )}
    </div>
  );
};

export default Cart;
