import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from '../services/api';
import FeaturedProperties from '../components/FeaturedProperties';
import '../styles/Home.css';
import '../styles/Navbar.css';
import '../styles/Footer.css';

const Home = () => {
  const [featuredProperties, setFeaturedProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('authToken');
      setIsLoggedIn(!!token);
    };

    checkAuthStatus();
  }, []);

  useEffect(() => {
    const fetchFeaturedProperties = async () => {
      try {
        const response = await axios.get('/api/properties/featured');
        setFeaturedProperties(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load featured properties');
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProperties();
  }, []);

  const handleDatabaseOperation = (operation) => {
    if (!isLoggedIn) {
      alert('Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ÿ≥ÿ¨ŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÑŸÑŸÇŸäÿßŸÖ ÿ®Ÿáÿ∞Ÿá ÿßŸÑÿπŸÖŸÑŸäÿ©');
      navigate('/login');
      return false;
    }
    
    return true;
  };

  const handleSaveProperty = (propertyId) => {
    if (handleDatabaseOperation('save')) {
      console.log(`Saving property ${propertyId} to favorites`);
    }
  };

  const testimonials = [
    {
      id: 1,
      name: "Ahmed Mohamed",
      comment: "Found my dream apartment within days. Excellent service and support throughout the process!",
      rating: 5,
      avatar: "https://randomuser.me/api/portraits/men/32.jpg"
    },
    {
      id: 2,
      name: "Sara Ali",
      comment: "The team was very professional and helped me find a perfect home for my family. Highly recommended!",
      rating: 4,
      avatar: "https://randomuser.me/api/portraits/women/44.jpg"
    },
    {
      id: 3,
      name: "Youssef Ibrahim",
      comment: "Great platform with a wide selection of properties. Made my search so much easier.",
      rating: 5,
      avatar: "https://randomuser.me/api/portraits/men/75.jpg"
    },
    {
      id: 4,
      name: "Fatima Hassan",
      comment: "Excellent customer service and quick response time. Found exactly what I was looking for!",
      rating: 5,
      avatar: "https://randomuser.me/api/portraits/women/63.jpg"
    }
  ];

  const services = [
    {
      id: 1,
      title: "Property Search",
      description: "Access thousands of verified properties with detailed information and photos.",
      icon: "üîç"
    },
    {
      id: 2,
      title: "Virtual Tours",
      description: "Explore properties from the comfort of your home with our virtual tour feature.",
      icon: "üè†"
    },
    {
      id: 3,
      title: "Expert Agents",
      description: "Our professional agents will guide you through every step of the rental process.",
      icon: "üëî"
    },
    {
      id: 4,
      title: "Secure Payments",
      description: "Safe and transparent payment process with multiple options available.",
      icon: "üí∞"
    },
    {
      id: 5,
      title: "24/7 Support",
      description: "Round-the-clock customer service to assist you whenever you need help.",
      icon: "üìû"
    }
  ];

  return (
    <div className="home-page">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Find Your Perfect Rental Home</h1>
          <p>Discover the best properties for rent in your desired location</p>
          <Link to="/properties" className="btn-primary">
            Browse Properties
          </Link>
        </div>
      </section>

      <section className="featured-properties">
        <div className="container">
          <h2>Featured Properties</h2>
          {loading ? (
            <div className="loading">Loading featured properties...</div>
          ) : error ? (
            <div className="error">{error}</div>
          ) : (
            <>
              <FeaturedProperties 
                properties={featuredProperties} 
                onSaveProperty={handleSaveProperty} 
              />
              <div className="text-center">
                <Link to="/properties" className="btn-secondary">
                  View All Properties
                </Link>
              </div>
            </>
          )}
        </div>
      </section>

      <section className="services-section">
        <div className="container">
          <h2>Our Services</h2>
          <p className="section-subtitle">We provide comprehensive solutions for all your rental needs</p>
          <div className="services-container">
            {services.map(service => (
              <div key={service.id} className="service-card">
                <div className="service-icon">{service.icon}</div>
                <h3>{service.title}</h3>
                <p>{service.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="testimonials-section">
        <div className="container">
          <h2>What Our Clients Say</h2>
          <p className="section-subtitle">Hear from people who found their perfect home through us</p>
          <div className="testimonials-container">
            {testimonials.map(testimonial => (
              <div key={testimonial.id} className="testimonial-card">
                <div className="rating">
                  {Array(testimonial.rating).fill().map((_, i) => (
                    <span key={i}>‚òÖ</span>
                  ))}
                </div>
                <p className="testimonial-text">"{testimonial.comment}"</p>
                <div className="client-info">
                  <img src={testimonial.avatar} alt={testimonial.name} className="client-avatar" />
                  <span className="client-name">{testimonial.name}</span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="cta-section">
        <div className="container">
          <h2>Ready to Find Your New Home?</h2>
          <p>Join thousands of satisfied clients who found their perfect rental property with us</p>
          <div className="cta-buttons">
            <Link to="/properties" className="btn-primary">
              Browse Listings
            </Link>
            <Link to="/contact" className="btn-secondary">
              Contact Us
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
