import React, { useState, useEffect } from "react";
import "../styles/AcceptPosts.css";

const AcceptPosts = () => {
  const [pendingPosts, setPendingPosts] = useState([]);

  useEffect(() => {
    const fetchPendingPosts = async () => {
      try {
        const response = await fetch("http://localhost:7080/admin/pending-posts");
        const data = await response.json();
        setPendingPosts(data);
      } catch (error) {
        alert("Error fetching pending posts");
      }
    };

    fetchPendingPosts();
  }, []);

  const handleApprovePost = (postId) => {
    alert(`Post with ID ${postId} has been approved`);
  };

  const handleRejectPost = (postId) => {
    alert(`Post with ID ${postId} has been rejected`);
  };

  return (
    <div className="admin-container">
      <h2>Approve Posts</h2>
      <table className="posts-table">
        <thead>
          <tr>
            <th>Post Title</th>
            <th>Description</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {pendingPosts.map((post) => (
            <tr key={post.id}>
              <td>{post.title}</td>
              <td>{post.description}</td>
              <td>
                <button className="approve-btn" onClick={() => handleApprovePost(post.id)}>Approve</button>
                <button className="reject-btn" onClick={() => handleRejectPost(post.id)}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AcceptPosts;
