import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import Footer from "../components/Footer";
import "../styles/Home.css";

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    axios.get("https://fakestoreapi.com/products")
      .then(response => {
        setProducts(response.data);
        setFilteredProducts(response.data);
      })
      .catch(error => console.log(error));
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    const filtered = products.filter(product =>
      product.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  return (
    <div className="home-container">
      <Navbar onSearch={handleSearch} />
      <Hero />

      {/* 🏷️ قسم البحث */}
      <div className="search-container">
        <input
          type="text"
          className="search-bar"
          placeholder="🔍 ابحث عن المنتجات..."
          value={searchQuery}
          onChange={(e) => handleSearch(e.target.value)}
        />
      </div>

      {/* ⭐ قسم العروض المميزة */}
      <section className="featured-section">
        <h2>🔥 عروض مميزة اليوم</h2>
        <div className="featured-images">
          <img src="https://source.unsplash.com/400x200/?sale,discount" alt="عرض 1" />
          <img src="https://source.unsplash.com/400x200/?electronics,deal" alt="عرض 2" />
          <img src="https://source.unsplash.com/400x200/?fashion,offer" alt="عرض 3" />
        </div>
      </section>

      {/* 📂 قسم الفئات */}
      <section className="categories-section">
        <h2>📂 تصفح حسب الفئة</h2>
        <div className="categories">
          <div className="category-card">
            <img src="https://source.unsplash.com/100x100/?clothes" alt="ملابس" />
            <p>👕 ملابس</p>
          </div>
          <div className="category-card">
            <img src="https://source.unsplash.com/100x100/?electronics" alt="إلكترونيات" />
            <p>📱 إلكترونيات</p>
          </div>
          <div className="category-card">
            <img src="https://source.unsplash.com/100x100/?shoes" alt="أحذية" />
            <p>👟 أحذية</p>
          </div>
          <div className="category-card">
            <img src="https://source.unsplash.com/100x100/?watches" alt="ساعات" />
            <p>⌚ ساعات</p>
          </div>
        </div>
      </section>

      {/* 🛍️ قسم المنتجات */}
      <h1 className="home-title">🛍 جميع المنتجات</h1>
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <ProductCard key={product.id} product={product} />
          ))
        ) : (
          <p className="no-products">❌ لا توجد منتجات مطابقة لبحثك</p>
        )}
      </div>

      {/* 💬 قسم مراجعات العملاء */}
      <section className="reviews-section">
        <h2>💬 آراء عملائنا</h2>
        <div className="reviews">
          <div className="review-card">
            <p>"منتجات رائعة وأسعار ممتازة! 👌"</p>
            <span>- أحمد، السعودية</span>
          </div>
          <div className="review-card">
            <p>"أسرع شحن وتجربة شراء مذهلة! 🚀"</p>
            <span>- فاطمة، الإمارات</span>
          </div>
          <div className="review-card">
            <p>"خدمة العملاء متعاونة جدًا. ❤️"</p>
            <span>- خالد، مصر</span>
          </div>
        </div>
      </section>

      {/* 🏁 الفوتر */}
      <Footer />
    </div>
  );
};

export default Home;
