import React, { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import ProductDetails from "./pages/ProductDetails";
import Cart from "./pages/Cart";
import Login from "./pages/Login";
import Wishlist from "./pages/Wishlist";
import Profile from "./pages/Profile";
import Checkout from "./pages/Checkout";
import Categories from "./pages/Categories";
import About from "./pages/About";
import Contact from "./pages/Contact";

const ProtectedRoute = ({ element }) => {
  const user = localStorage.getItem("user"); // التحقق من المستخدم المسجل
  return user ? element : <Navigate to="/login" replace />;
};

const App = () => {
  const [cart, setCart] = useState([]); // حالة السلة

  // دالة لإضافة منتج إلى السلة
  const addToCart = (product) => {
    setCart((prevCart) => {
      const isProductInCart = prevCart.some((item) => item.id === product.id);
      return isProductInCart ? prevCart : [...prevCart, product];
    });
  };

  return (
    <>
      <Navbar cartCount={cart.length} /> {/* عرض عدد المنتجات في السلة */}
      <Routes>
        <Route path="/" element={<ProtectedRoute element={<Home addToCart={addToCart} />} />} />
        <Route path="/product/:id" element={<ProtectedRoute element={<ProductDetails addToCart={addToCart} />} />} />
        <Route path="/cart" element={<ProtectedRoute element={<Cart cart={cart} />} />} />
        <Route path="/wishlist" element={<ProtectedRoute element={<Wishlist />} />} />
        <Route path="/profile" element={<ProtectedRoute element={<Profile />} />} />
        <Route path="/checkout" element={<ProtectedRoute element={<Checkout cart={cart} />} />} />
        <Route path="/categories" element={<ProtectedRoute element={<Categories />} />} />
        <Route path="/about" element={<ProtectedRoute element={<About />} />} />
        <Route path="/contact" element={<ProtectedRoute element={<Contact />} />} />
        <Route path="/login" element={<Login />} />
      </Routes>
    </>
  );
};

export default App;
