import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/Login.css";

const Login = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: '',
    password: '',
    username: '',
    phone: '',
    confirmPassword: '',
    userType: 'tenant',
    propertyOwnership: '',
  });

  const [errors, setErrors] = useState({});
  const [isLogin, setIsLogin] = useState(true);

  // بيانات وهمية محلية
  const fakeUsers = [
    {
      email: 'tenant.test@example.com',
      password: 'Test@1234',
      userType: 'tenant'
    },
    {
      email: 'admin@example.com',
      password: 'Admin123',
      userType: 'admin'
    },
    {
      email: 'landlord@example.com',
      password: 'Landlord@123',
      userType: 'landlord'
    }
  ];

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: files ? files[0] : value,
    }));
  };

  const validateForm = () => {
    let formErrors = {};
    let isValid = true;

    if (!formData.email) {
      formErrors.email = "Email is required.";
      isValid = false;
    }

    if (!formData.password) {
      formErrors.password = "Password is required.";
      isValid = false;
    }

    if (!isLogin && formData.password !== formData.confirmPassword) {
      formErrors.confirmPassword = "Passwords do not match.";
      isValid = false;
    }

    if (!isLogin && formData.userType === 'landlord' && !formData.propertyOwnership) {
      formErrors.propertyOwnership = "Property ownership certificate is required.";
      isValid = false;
    }

    setErrors(formErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const foundUser = fakeUsers.find(
        user => user.email === formData.email && user.password === formData.password
      );

      if (isLogin) {
        if (formData.userType === 'guest') {
          alert('You are logged in as a Guest.');
          navigate('/Gust');
          return;
        }

        if (foundUser) {
          alert('Login successful (from fake data)!');

          if (foundUser.userType === 'admin') {
            navigate('/admin-dashboard');
          } else if (foundUser.userType === 'landlord') {
            navigate('/landlord-dashboard');
          } else if (foundUser.userType === 'tenant') {
            navigate('/tenant-dashboard');
          }
        } else {
          alert('Invalid email or password.');
        }
      } else {
        // هنا نسوي "تسجيل" وهمي فقط
        alert('Fake registration successful!');
        navigate('/tenant-dashboard');
      }
    }
  };

  const loginAsFakeTenant = () => {
    setFormData({
      email: 'tenant.test@example.com',
      password: 'Test@1234',
      username: '',
      phone: '',
      confirmPassword: '',
      userType: 'tenant',
      propertyOwnership: '',
    });
    setIsLogin(true);

    setTimeout(() => {
      document.querySelector("form").dispatchEvent(
        new Event("submit", { cancelable: true, bubbles: true })
      );
    }, 100);
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h2>{isLogin ? 'Login' : 'Register'}</h2>
        <form onSubmit={handleSubmit}>
          <div>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Email"
            />
            {errors.email && <div className="error">{errors.email}</div>}
          </div>

          {!isLogin && (
            <div>
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="Username"
              />
              {errors.username && <div className="error">{errors.username}</div>}
            </div>
          )}

          <div>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Password"
            />
            {errors.password && <div className="error">{errors.password}</div>}
          </div>

          {!isLogin && (
            <div>
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                placeholder="Confirm Password"
              />
              {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
            </div>
          )}

          {!isLogin && (
            <div>
              <select name="userType" value={formData.userType} onChange={handleInputChange}>
                <option value="tenant">Tenant</option>
                <option value="landlord">Landlord</option>
              </select>
            </div>
          )}

          {formData.userType === 'landlord' && !isLogin && (
            <div>
              <label>Upload Property Ownership Certificate:</label>
              <input
                type="file"
                name="propertyOwnership"
                onChange={handleInputChange}
              />
              {errors.propertyOwnership && <div className="error">{errors.propertyOwnership}</div>}
            </div>
          )}

          <button type="submit" className="btn">
            {isLogin ? 'Login' : 'Register'}
          </button>
        </form>

        <p>
          {isLogin ? "Don't have an account? " : 'Already have an account? '}
          <span onClick={() => setIsLogin(!isLogin)} className="toggle-link">
            {isLogin ? 'Register' : 'Login'}
          </span>
        </p>

        <p>
          <span onClick={() => navigate('/Gust')} className="toggle-link">
            Login as Guest
          </span>
        </p>

        <p>
          <span onClick={loginAsFakeTenant} className="toggle-link">
            تسجيل دخول كمستأجر وهمي
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
