import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../../services/api';

const PendingApprovals = () => {
  const [pendingLandlords, setPendingLandlords] = useState([]);
  const [pendingProperties, setPendingProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('landlords');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPendingItems = async () => {
      try {
        setLoading(true);
        const [landlordsRes, propertiesRes] = await Promise.all([
          axios.get('/admin/pending-landlords'),
          axios.get('/admin/pending-properties')
        ]);
        
        setPendingLandlords(landlordsRes.data);
        setPendingProperties(propertiesRes.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load pending approvals');
      } finally {
        setLoading(false);
      }
    };
    
    fetchPendingItems();
  }, []);

  const handleLandlordApproval = async (id, action) => {
    try {
      await axios.put(`/admin/landlords/${id}/${action}`);
      setPendingLandlords(pendingLandlords.filter(landlord => landlord._id !== id));
    } catch (err) {
      setError(err.response?.data?.message || `Failed to ${action} landlord`);
    }
  };

  const handlePropertyApproval = async (id, action) => {
    try {
      await axios.put(`/admin/properties/${id}/${action}`);
      setPendingProperties(pendingProperties.filter(property => property._id !== id));
    } catch (err) {
      setError(err.response?.data?.message || `Failed to ${action} property`);
    }
  };

  const viewLandlordDetails = (id) => {
    navigate(`/admin/landlord-applications/${id}`);
  };

  const viewPropertyDetails = (id) => {
    navigate(`/properties/${id}`);
  };

  if (loading) return (
    <div className="loading-container">
      <div className="spinner"></div>
      <p>Loading pending approvals...</p>
    </div>
  );

  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="admin-approvals-container">
      <h1>Pending Approvals</h1>
      
      <div className="approval-tabs">
        <button 
          className={`tab-button ${activeTab === 'landlords' ? 'active' : ''}`}
          onClick={() => setActiveTab('landlords')}
        >
          Landlord Registrations ({pendingLandlords.length})
        </button>
        <button 
          className={`tab-button ${activeTab === 'properties' ? 'active' : ''}`}
          onClick={() => setActiveTab('properties')}
        >
          Property Listings ({pendingProperties.length})
        </button>
      </div>

      {activeTab === 'landlords' ? (
        <div className="landlords-section">
          <h2>Pending Landlord Accounts</h2>
          {pendingLandlords.length > 0 ? (
            <div className="landlords-grid">
              {pendingLandlords.map(landlord => (
                <div key={landlord._id} className="approval-card">
                  <div className="card-header">
                    <h3>{landlord.name}</h3>
                    <span className="pending-badge">Pending</span>
                  </div>
                  
                  <div className="card-body">
                    <p><strong>Email:</strong> {landlord.email}</p>
                    <p><strong>Phone:</strong> {landlord.phone}</p>
                    <p><strong>Registered:</strong> {new Date(landlord.createdAt).toLocaleDateString()}</p>
                  </div>
                  
                  <div className="card-actions">
                    <button 
                      onClick={() => viewLandlordDetails(landlord._id)}
                      className="details-button"
                    >
                      View Details
                    </button>
                    <div className="approval-buttons">
                      <button 
                        onClick={() => handleLandlordApproval(landlord._id, 'approve')}
                        className="approve-button"
                      >
                        Approve
                      </button>
                      <button 
                        onClick={() => handleLandlordApproval(landlord._id, 'reject')}
                        className="reject-button"
                      >
                        Reject
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-items">
              <p>No pending landlord registrations</p>
            </div>
          )}
        </div>
      ) : (
        <div className="properties-section">
          <h2>Pending Property Listings</h2>
          {pendingProperties.length > 0 ? (
            <div className="properties-grid">
              {pendingProperties.map(property => (
                <div key={property._id} className="approval-card">
                  <div className="card-header">
                    <h3>{property.title}</h3>
                    <span className="pending-badge">Pending</span>
                  </div>
                  
                  <div className="card-body">
                    <p><strong>Price:</strong> ${property.price}/month</p>
                    <p><strong>Location:</strong> {property.location}</p>
                    <p><strong>Posted by:</strong> {property.landlord.name}</p>
                    <p><strong>Submitted:</strong> {new Date(property.createdAt).toLocaleDateString()}</p>
                  </div>
                  
                  <div className="card-actions">
                    <button 
                      onClick={() => viewPropertyDetails(property._id)}
                      className="details-button"
                    >
                      View Property
                    </button>
                    <div className="approval-buttons">
                      <button 
                        onClick={() => handlePropertyApproval(property._id, 'approve')}
                        className="approve-button"
                      >
                        Approve
                      </button>
                      <button 
                        onClick={() => handlePropertyApproval(property._id, 'reject')}
                        className="reject-button"
                      >
                        Reject
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-items">
              <p>No pending property listings</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PendingApprovals;
