import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { logout } from "../redux/authSlice"; // Make sure you have this action in your Redux slice
import "../styles/Profile.css";

const Profile = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.auth.user);

  // Local state for editing profile
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: user?.username || "",
    email: user?.email || "",
    phone: user?.phone || "",
    address: user?.address || "",
  });

  if (!user) return <p className="text-center">üö™ Please log in to view your profile.</p>;

  // Handle form input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Toggle edit mode
  const toggleEdit = () => {
    setIsEditing(!isEditing);
  };

  // Handle logout
  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <div className="profile-container">
      <h1>üë§ Welcome, {user.username}!</h1>

      {isEditing ? (
        <div className="edit-profile">
          <input type="text" name="username" value={formData.username} onChange={handleChange} />
          <input type="email" name="email" value={formData.email} onChange={handleChange} />
          <input type="text" name="phone" value={formData.phone} onChange={handleChange} />
          <input type="text" name="address" value={formData.address} onChange={handleChange} />
          <button onClick={toggleEdit}>üíæ Save</button>
        </div>
      ) : (
        <div className="profile-info">
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>üìû Phone:</strong> {user.phone || "Not Provided"}</p>
          <p><strong>üìç Address:</strong> {user.address || "Not Provided"}</p>
          <button onClick={toggleEdit}>‚úèÔ∏è Edit Profile</button>
        </div>
      )}

      <button className="logout-btn" onClick={handleLogout}>üö™ Logout</button>
    </div>
  );
};

export default Profile;
