import { useContext, useState, useEffect } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import axios from '../services/api';

const AdminRoute = () => {
  const { user, loading: authLoading } = useContext(AuthContext);
  const [isValidAdmin, setIsValidAdmin] = useState(null);
  const [verificationLoading, setVerificationLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const verifyAdmin = async () => {
      if (!user) {
        setVerificationLoading(false);
        return;
      }

      try {
        setVerificationLoading(true);
        const response = await axios.get('/admin/verify'); // Removed /api prefix since baseURL already includes it
        setIsValidAdmin(response.data.isValid);
      } catch (error) {
        setError(error.response?.data?.message || 'Administrator verification failed');
        setIsValidAdmin(false);
      } finally {
        setVerificationLoading(false);
      }
    };

    verifyAdmin();
  }, [user]);

  // Show loading state while checking auth or verifying
  if (authLoading || verificationLoading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mb-4"></div>
        <p className="text-gray-600">
          {verificationLoading ? 'Verifying administrator privileges...' : 'Loading authentication...'}
        </p>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" state={{ from: 'admin-route' }} replace />;
  }

  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="bg-red-50 border-l-4 border-red-500 p-4 max-w-md">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-500" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">
                {error}
                <br />
                <span className="font-medium">Redirecting you to the homepage...</span>
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (isValidAdmin === false) {
    return <Navigate to="/" replace />;
  }

  return <Outlet />;
};

export default AdminRoute;
