import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  fetchProperty,
  incrementPropertyViews,
  submitPropertyApplication,
  savePropertyToFavorites,
  fetchPropertyComments,
  addPropertyComment
} from '../services/api';
import AuthContext from '../context/AuthContext';
import { useContext } from 'react';

const PropertyDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showApplicationForm, setShowApplicationForm] = useState(false);
  const [application, setApplication] = useState({
    message: '',
    documents: []
  });
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [isSaved, setIsSaved] = useState(false);
  const isGuest = !user && localStorage.getItem('guestMode');

  useEffect(() => {
    const loadPropertyData = async () => {
      try {
        setLoading(true);
        const [propertyRes, commentsRes] = await Promise.all([
          fetchProperty(id),
          fetchPropertyComments(id)
        ]);

        setProperty(propertyRes.data);
        setComments(commentsRes.data);
        setIsSaved(propertyRes.data.isSaved || false);

        if (!isGuest) {
          await incrementPropertyViews(id);
        }
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load property details');
      } finally {
        setLoading(false);
      }
    };

    loadPropertyData();
  }, [id, isGuest]);

  const handleApplicationSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      navigate('/login', {
        state: {
          message: 'Please login to submit an application',
          returnUrl: `/properties/${id}`
        }
      });
      return;
    }

    try {
      const formData = new FormData();
      formData.append('message', application.message);
      application.documents.forEach(doc => {
        formData.append('documents', doc);
      });

      await submitPropertyApplication(id, formData);
      setShowApplicationForm(false);
      setApplication({ message: '', documents: [] });
      setError(null);
      alert('Application submitted successfully!');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit application');
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!user) {
      navigate('/login', {
        state: {
          message: 'Please login to post a comment',
          returnUrl: `/properties/${id}`
        }
      });
      return;
    }

    try {
      const res = await addPropertyComment(id, { text: newComment });
      setComments([...comments, res.data]);
      setNewComment('');
      setError(null);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to post comment');
    }
  };

  const toggleSaveProperty = async () => {
    if (!user) {
      navigate('/login', {
        state: {
          message: 'Please login to save properties',
          returnUrl: `/properties/${id}`
        }
      });
      return;
    }

    try {
      if (isSaved) {
        await savePropertyToFavorites(id, 'remove');
        setIsSaved(false);
      } else {
        await savePropertyToFavorites(id, 'add');
        setIsSaved(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update saved properties');
    }
  };

  const handleRequiresLogin = (action) => {
    navigate('/login', {
      state: {
        message: `Please login to ${action}`,
        returnUrl: `/properties/${id}`
      }
    });
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading property details...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!property) {
    return <div className="not-found">Property not found</div>;
  }

  {
    user?.role === 'Landlord' && property?.status === 'available' && (
      <button
        onClick={() => markAsSold(property._id)}
        className="sold-button"
      >
        Mark as Sold
      </button>
    )
  }



  return (
    <div className="property-detail-container">
      {isGuest && (
        <div className="guest-notification">
          <p>You are browsing as a guest.
            <button onClick={() => navigate('/register')}>Register</button> or
            <button onClick={() => navigate('/login')}>Login</button> for full access.
          </p>
        </div>
      )}

      <div className="property-gallery">
        {property.images.map((image, index) => (
          <div key={index} className="gallery-item">
            <img
              src={`/uploads/${image}`}
              alt={`${property.title} - ${index + 1}`}
              onClick={() => window.open(`/uploads/${image}`, '_blank')}
            />
          </div>
        ))}
      </div>

      <div className="property-main">
        <div className="property-header">
          <h1>{property.title}</h1>
          <div className="property-meta">
            <span className="price">${property.price}/month</span>
            <span className="location">{property.location}</span>
            <span className="views">{property.viewCount} views</span>
            <span className={`status ${property.status}`}>
              {property.status.toUpperCase()}
            </span>
          </div>
        </div>

        <div className="property-owner">
          <h3>Listed by: {property.landlord.name}</h3>
          <button
            className="contact-owner"
            onClick={() => user ? navigate('/messages') : handleRequiresLogin('contact the owner')}
          >
            Contact Owner
          </button>
        </div>

        <div className="property-description">
          <h2>Description</h2>
          <p>{property.description}</p>
        </div>

        <div className="property-actions">
          {isGuest ? (
            <>
              <button
                className="apply-btn guest"
                onClick={() => handleRequiresLogin('apply for rental')}
              >
                Apply for Rental
              </button>
              <button
                className="save-btn guest"
                onClick={() => handleRequiresLogin('save property')}
              >
                Save Property
              </button>
            </>
          ) : user?.role === 'tenant' && (
            <>
              <button
                className={`apply-btn ${showApplicationForm ? 'cancel' : ''}`}
                onClick={() => setShowApplicationForm(!showApplicationForm)}
              >
                {showApplicationForm ? 'Cancel Application' : 'Apply for Rental'}
              </button>
              <button
                className={`save-btn ${isSaved ? 'saved' : ''}`}
                onClick={toggleSaveProperty}
              >
                {isSaved ? 'Saved' : 'Save Property'}
              </button>
            </>
          )}
        </div>

        {showApplicationForm && (
          <form className="application-form" onSubmit={handleApplicationSubmit}>
            <h3>Rental Application Form</h3>

            <div className="form-group">
              <label htmlFor="application-message">Message to Landlord</label>
              <textarea
                id="application-message"
                name="message"
                value={application.message}
                onChange={(e) => setApplication({ ...application, message: e.target.value })}
                placeholder="Tell the landlord why you'd be a great tenant..."
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="application-documents">Required Documents</label>
              <input
                id="application-documents"
                type="file"
                multiple
                onChange={(e) => setApplication({ ...application, documents: [...e.target.files] })}
                accept=".pdf,.jpg,.jpeg,.png"
                required
              />
              <p className="hint">Upload ID, proof of income, and rental history (PDF, JPG, PNG)</p>
            </div>

            <button type="submit" className="submit-btn">
              Submit Application
            </button>
          </form>
        )}
      </div>

      <div className="property-comments">
        <h2>Comments ({comments.length})</h2>

        <div className="comments-list">
          {comments.length > 0 ? (
            comments.map(comment => (
              <div key={comment._id} className="comment-card">
                <div className="comment-header">
                  <img
                    src={comment.author.avatar || '/images/default-avatar.png'}
                    alt={comment.author.name}
                    className="comment-avatar"
                  />
                  <div>
                    <h4>{comment.author.name}</h4>
                    <p className="comment-date">
                      {new Date(comment.createdAt).toLocaleString()}
                    </p>
                  </div>
                </div>
                <div className="comment-body">
                  <p>{comment.text}</p>
                </div>
              </div>
            ))
          ) : (
            <p className="no-comments">No comments yet. Be the first to comment!</p>
          )}
        </div>

        <div className={`property-status ${property.status}`}>
          {property.status === 'sold' ? 'تم البيع' :
            property.status === 'rented' ? 'تم التأجير' : 'متاح'}
        </div>

        {isGuest ? (
          <button
            className="comment-btn guest"
            onClick={() => handleRequiresLogin('add a comment')}
          >
            Login to Post a Comment
          </button>
        ) : (
          <form className="comment-form" onSubmit={handleCommentSubmit}>
            <textarea
              placeholder="Write your comment here..."
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              required
            />
            <button type="submit" className="comment-btn">
              Post Comment
            </button>
          </form>
        )}
      </div>
    </div>
  );
};

export default PropertyDetail;
