import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; 

const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [showSearch, setShowSearch] = useState(false); // ุงูุชุญูู ูู ุฅุธูุงุฑ ุงูุจุญุซ ุจุงูุฌูุงู

  // ุชุฃุซูุฑ ุงูุชูุฑูุฑ
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      onSearch(search);
    }
  };

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      {/* ุดุนุงุฑ ุงููุชุฌุฑ */}
      <Link to="/" className="navbar-logo">
        ๐๏ธ <span>Shopwise</span>
      </Link>

      {/* ุฒุฑ ุงูุจุญุซ ูู ุงูุฌูุงู */}
      <button className="mobile-search-btn" onClick={() => setShowSearch(!showSearch)}>๐</button>

      {/* ุตูุฏูู ุงูุจุญุซ */}
      <form onSubmit={handleSearch} className={`search-box ${showSearch ? "show" : ""}`}>
        <input
          type="text"
          placeholder="๐ Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit">๐</button>
        {search && <button type="button" onClick={() => setSearch("")} className="clear-btn">โ</button>}
        <button type="button" className="close-search" onClick={() => setShowSearch(false)}>โ</button>
      </form>

      {/* ูุงุฆูุฉ ุงูุฑูุงุจุท */}
      <ul className={`navbar-links ${menuOpen ? "active" : ""}`}>
        <li><Link to="/categories">๐ Categories</Link></li>
        <li><Link to="/contact">๐ Contact Us</Link></li>
        <li><Link to="/cart">๐ Cart</Link></li>
        <li><Link to="/profile">๐ค Profile</Link></li>
        <li><Link to="/login" className="sign-in-btn">๐ Sign in</Link></li>
      </ul>

      {/* ุฒุฑ ุงููุงุฆูุฉ ููุฌูุงู */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? "โ" : "โฐ"}
      </div>
    </nav>
  );
};

export default Navbar;
