import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; 

const Navbar = ({ onSearch }) => {
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // ÿ™ÿ£ÿ´Ÿäÿ± ÿßŸÑÿ™ŸÖÿ±Ÿäÿ± ŸÑÿ•ÿ∂ÿßŸÅÿ© ÿ∏ŸÑ ŸÑŸÑÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿπŸÑŸàŸä
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (search.trim() !== "") {
      onSearch(search);
    }
  };

  return (
    <nav className={`navbar ${scrolled ? "scrolled" : ""}`}>
      {/* ÿ¥ÿπÿßÿ± ÿßŸÑŸÖÿ™ÿ¨ÿ± */}
      <Link to="/" className="navbar-logo">
        üõçÔ∏è <span>Shopwise</span>
      </Link>

      {/* ÿµŸÜÿØŸàŸÇ ÿßŸÑÿ®ÿ≠ÿ´ */}
      <form onSubmit={handleSearch} className="search-box">
        <input
          type="text"
          placeholder="üîç Search for products..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button type="submit">Search</button>
        {search && <button type="button" onClick={() => setSearch("")} className="clear-btn">‚úñ</button>}
      </form>

      {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ±Ÿàÿßÿ®ÿ∑ */}
      <ul className={`navbar-links ${menuOpen ? "active" : ""}`}>
        <li><Link to="/categories">üìÇ Categories</Link></li>
        <li><Link to="/contact">üìû Contact Us</Link></li>
        <li><Link to="/cart">üõí Cart</Link></li>
        <li><Link to="/profile">üë§ Profile</Link></li>
        <li><Link to="/login" className="sign-in-btn">üîë Sign in</Link></li>
      </ul>

      {/* ÿ≤ÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ŸÑŸÑÿ¨ŸàÿßŸÑ */}
      <div className="menu-icon" onClick={() => setMenuOpen(!menuOpen)}>
        {menuOpen ? "‚úñ" : "‚ò∞"}
      </div>
    </nav>
  );
};

export default Navbar;
