import { useContext, useState, useEffect } from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import AuthContext from '../context/AuthContext';
import axios from '../../services/api';

const LandlordRoute = () => {
  const { user, loading } = useContext(AuthContext);
  const [isValidLandlord, setIsValidLandlord] = useState(null);

  useEffect(() => {
    const verifyLandlord = async () => {
      if (user) {
        try {
           const response = await axios.get('/api/landlord/verify', {
            headers: {
              Authorization: `Bearer ${user.token}`
            }
          });
          setIsValidLandlord(response.data.isValid);
        } catch (error) {
          setIsValidLandlord(false);
        }
      }
    };

    verifyLandlord();
  }, [user]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (isValidLandlord === false) {
    return <Navigate to="/" />;
  }

  if (isValidLandlord === null) {
    return <div>Verifying your account...</div>;
  }

  return <Outlet />;
};

export default LandlordRoute;
