import React, { useEffect, useState } from "react";
import axios from "axios";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    axios
      .get("https://fakestoreapi.com/products/categories")
      .then((response) => setCategories(response.data))
      .catch((error) => console.log(error));
  }, []);

  const fetchProducts = (category) => {
    setSelectedCategory(category);
    axios
      .get(`https://fakestoreapi.com/products/category/${category}`)
      .then((response) => setProducts(response.data))
      .catch((error) => console.log(error));
  };

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold text-center mb-6">üõçÔ∏è Categories</h1>

      {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÅÿ¶ÿßÿ™ */}
      <div className="flex flex-wrap justify-center gap-4 mb-6">
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => fetchProducts(category)}
            className={`px-6 py-3 rounded-lg shadow-md transition-all duration-300 ${
              selectedCategory === category
                ? "bg-blue-600 text-white"
                : "bg-gray-200 hover:bg-blue-500 hover:text-white"
            }`}
          >
            {category.toUpperCase()}
          </button>
        ))}
      </div>

      {/* ÿπÿ±ÿ∂ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™ */}
      {selectedCategory && (
        <div>
          <h2 className="text-2xl font-semibold text-center mb-4">
            üõí Products in {selectedCategory.toUpperCase()}
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {products.map((product) => (
              <div key={product.id} className="border rounded-lg p-4 shadow-md hover:shadow-lg transition">
                <img
                  src={product.image}
                  alt={product.title}
                  className="w-full h-40 object-contain mb-3"
                />
                <h3 className="text-lg font-medium">{product.title}</h3>
                <p className="text-green-600 font-semibold">${product.price}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Categories;
