import React, { useEffect, useState } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/Home.css";

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    axios.get("https://fakestoreapi.com/products")
      .then(response => {
        setProducts(response.data);
        setFilteredProducts(response.data);
        const uniqueCategories = ["all", ...new Set(response.data.map(item => item.category))];
        setCategories(uniqueCategories);
        setLoading(false);
      })
      .catch(err => {
        setError(" Failed to load products. Please try again later.");
        setLoading(false);
      });
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query);
    filterProducts(query, selectedCategory);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    filterProducts(searchQuery, category);
  };

  const filterProducts = (query, category) => {
    let filtered = products.filter(product =>
      product.title.toLowerCase().includes(query.toLowerCase())
    );
    if (category !== "all") {
      filtered = filtered.filter(product => product.category === category);
    }
    setFilteredProducts(filtered);
  };

  return (
    <div className="home-container">
      <Navbar onSearch={handleSearch} />

      {/* Banner Section */}
      <div className="banner">
        <h1>Welcome to Our Store</h1>
        <p>Discover the best products at unbeatable prices</p>
              </div>

      {/* Filters Section */}
      <div className="filters-container">
        <div className="search-box">
          <span className="icon">üîç</span>
          <input
            type="text"
            className="search-bar"
            placeholder="Search for products..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
          />
        </div>

        <div className="category-box">
          <span className="icon">üìÇ</span>
          <select className="category-filter" value={selectedCategory} onChange={(e) => handleCategoryChange(e.target.value)}>
            {categories.map((category, index) => (
              <option key={index} value={category}>
                {category === "all" ? "üìå All Categories" : category}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Loading and Error Handling */}
      {loading ? (
        <p className="loading">‚è≥ Loading products...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : (
        <>
          <h1 className="home-title">üõç All Products ({filteredProducts.length})</h1>
          <div className="product-grid">
            {filteredProducts.length > 0 ? (
              filteredProducts.map(product => (
                <ProductCard key={product.id} product={product} />
              ))
            ) : (
              <p className="no-products">‚ùå No matching products found</p>
            )}
          </div>
        </>
      )}

      {/* Customer Reviews Section */}
      <div className="reviews-section">
        <h2>What Our Customers Say</h2>
        <div className="reviews">
          <div className="review-card">
            <p>"Amazing quality! Highly recommend this store!"</p>
            <div className="stars">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</div>
          </div>
          <div className="review-card">
            <p>"Fast delivery and great customer service!"</p>
            <div className="stars">‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</div>
          </div>
        </div>
      </div>

      {/* Footer Section */}
      <Footer />
    </div>
  );
};

export default Home;
