//Admi
import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { approveLandlord, rejectLandlord, fetchLandlordApplication } from '../services/api';

const LandlordApplicationDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await fetchLandlordApplication(id);
        setApplication(res.data);
      } catch (error) {
        setMessage('Failed to load application details');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [id]);

  const handleApprove = async () => {
    try {
      await approveLandlord(id);
      navigate('/admin/pending-landlords');
    } catch (error) {
      setMessage('Failed to approve application');
    }
  };

  const handleReject = async () => {
    try {
      await rejectLandlord(id);
      navigate('/admin/pending-landlords');
    } catch (error) {
      setMessage('Failed to reject application');
    }
  };

  if (loading) return <div className="loading">Loading application details...</div>;

  return (
    <div className="landlord-application-detail">
      <h2>Landlord Application Details</h2>
      {message && <div className="alert alert-danger">{message}</div>}
      
      <div className="application-card">
        <div className="application-header">
          <h3>{application.name}</h3>
          <p className="application-date">
            Applied on: {new Date(application.createdAt).toLocaleDateString()}
          </p>
        </div>
        
        <div className="application-body">
          <div className="detail-row">
            <span className="detail-label">Email:</span>
            <span className="detail-value">{application.email}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Phone:</span>
            <span className="detail-value">{application.phone}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Address:</span>
            <span className="detail-value">{application.address}</span>
          </div>
          <div className="detail-row">
            <span className="detail-label">Status:</span>
            <span className={`status-badge ${application.status}`}>
              {application.status}
            </span>
          </div>
        </div>
        
        <div className="application-actions">
          <button 
            onClick={handleApprove}
            className="btn btn-success action-btn"
          >
            Approve Application
          </button>
          <button 
            onClick={handleReject}
            className="btn btn-danger action-btn"
          >
            Reject Application
          </button>
        </div>
      </div>
    </div>
  );
};

export default LandlordApplicationDetail;
