import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import '../styles/Login.css';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));

    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.email || !formData.password) {
      setMessage('Please enter both email and password.');
      return;
    }

    setIsLoading(true);
    setMessage('');

    try {
      const res = await axios.post('https://localhost:44357/api/Auth/login', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const loggedInUser = res.data;

      if (loggedInUser.token && loggedInUser.user) {
        localStorage.setItem(
          'profile',
          JSON.stringify({
            token: loggedInUser.token,
            user: loggedInUser.user,
          })
        );

        // Redirect based on role
        switch (loggedInUser.user.role) {
          case 'Admin':
            navigate('/admin/dashboard');
            break;
          case 'Landlord':
            navigate('/landlord/dashboard');
            break;
          case 'Tenant':
            navigate('/tenant/dashboard');
            break;
          default:
            navigate('/');
        }
      } else {
        setMessage('Invalid login response. Please try again.');
      }
    } catch (error) {
      if (error.response) {
        setMessage(error.response.data.message || 'Login failed');
      } else if (error.request) {
        setMessage('No response from server. Please try again later.');
      } else {
        setMessage('An error occurred during the login process.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign In</h2>

      {message && <div className="message error">{message}</div>}

      <form onSubmit={handleSubmit} noValidate>
        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" disabled={isLoading}>
          {isLoading ? 'Logging in...' : 'Login'}
        </button>
      </form>

      <p className="auth-footer">
        Don't have an account? <Link to="/register">Sign up</Link>
      </p>
    </div>
  );
};

export default Login;
