import { createContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkAuth = () => {
      const storedProfile = localStorage.getItem('profile');
      if (!storedProfile) {
        localStorage.removeItem('profile');
      }
      setLoading(false);
    };
    checkAuth();
  }, []);

  const login = async (credentials) => {
    try {
      const res = await axios.post('https://localhost:44357/api/Auth/registerlogin', credentials);
      const { user } = res.data;

      localStorage.setItem('profile', JSON.stringify({ user }));
      return { user };
    } catch (error) {
      throw error;
    }
  };

  const register = async (userData) => {
    const res = await axios.post('https://localhost:44357/api/Auth/register', userData);
    return res.data;
  };

  const logout = async () => {
    await axios.post('/api/auth/logout');
    localStorage.removeItem('profile');
  };

  return (
    <AuthContext.Provider value={{ loading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
