import { Link } from 'react-router-dom';

const PropertyCard = ({ property, onAction, isGuest = false, user }) => {
  const isSold = property.status === 'sold';
  const isRented = property.status === 'rented';

  return (
    <div className={`property-card ${isSold ? 'sold' : ''}`}>
      {(isSold || isRented) && (
        <div className={`property-badge ${isSold ? 'sold' : 'rented'}`}>
          {isSold ? 'تم البيع' : 'تم التأجير'}
        </div>
      )}
      
      <Link to={isGuest ? `/guest/properties/${property._id}` : `/properties/${property._id}`}>
        <div className="property-image-container">
          <img 
            src={`/uploads/${property.images[0]}`} 
            alt={property.title} 
            className={isSold ? 'sold-image' : ''}
          />
        </div>
        <div className="property-info">
          <h3>{property.title}</h3>
          <p className="price">
            ${property.price}
            {property.type === 'rent' ? '/month' : ''}
          </p>
          <p className="location">{property.location}</p>
          <p className="property-type">{property.propertyType}</p>
        </div>
      </Link>
      
      <div className="property-actions">
        {isSold || isRented ? (
          <div className="unavailable-message">
            هذا العقار غير متاح حالياً
          </div>
        ) : isGuest ? (
          <>
            <button onClick={() => onAction('save')} className="btn-save">
              Save (Login Required)
            </button>
            <button onClick={() => onAction('apply')} className="btn-apply">
              {property.type === 'rent' ? 'Apply' : 'Inquire'}
            </button>
          </>
        ) : (
          <>
            <button 
              onClick={() => onAction('save')} 
              className={`btn-save ${property.isSaved ? 'saved' : ''}`}
            >
              {property.isSaved ? 'Saved' : 'Save'}
            </button>
            <button 
              onClick={() => onAction(property.type === 'rent' ? 'apply' : 'inquire')} 
              className="btn-apply"
            >
              {property.type === 'rent' ? 'Apply' : 'Inquire'}
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default PropertyCard;
