import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "../styles/Checkout.css";

const Checkout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { cart, total } = location.state || { cart: [], total: 0 };

  // بيانات الدفع
  const [cardNumber, setCardNumber] = useState("");
  const [expiryDate, setExpiryDate] = useState("");
  const [cvv, setCvv] = useState("");
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    let formErrors = {};

    // تحقق من رقم البطاقة (يجب أن يكون 16 رقمًا)
    if (!/^\d{16}$/.test(cardNumber)) {
      formErrors.cardNumber = "Card number must be 16 digits";
    }

    // تحقق من تاريخ الانتهاء (MM/YY)
    if (!/^(0[1-9]|1[0-2])\/\d{2}$/.test(expiryDate)) {
      formErrors.expiryDate = "Enter a valid expiry date (MM/YY)";
    }

    // تحقق من CVV (3 أرقام)
    if (!/^\d{3}$/.test(cvv)) {
      formErrors.cvv = "CVV must be 3 digits";
    }

    setErrors(formErrors);
    return Object.keys(formErrors).length === 0;
  };

  const handlePayment = (e) => {
    e.preventDefault();
    if (validateForm()) {
      alert("Payment Successful! Thank you for your order.");
      navigate("/");
    }
  };

  return (
    <div className="checkout-container">
      <div className="checkout-card">
        <h2>Order Summary</h2>
        <div className="checkout-summary">
          {cart.map((item) => (
            <div key={item.id} className="checkout-item">
              <img src={item.image} alt={item.title} className="checkout-item-image" />
              <div className="checkout-item-details">
                <span>{item.title} (x{item.quantity})</span>
                <span className="checkout-price">${(item.price * item.quantity).toFixed(2)}</span>
              </div>
            </div>
          ))}
          <h3 className="checkout-total">Total: ${total}</h3>
        </div>

        <form className="payment-form" onSubmit={handlePayment}>
          <h2>Payment Details</h2>

          <label>Card Number</label>
          <input
            type="text"
            placeholder="1234 5678 9012 3456"
            value={cardNumber}
            onChange={(e) => setCardNumber(e.target.value)}
            maxLength="16"
          />
          {errors.cardNumber && <p className="error">{errors.cardNumber}</p>}

          <label>Expiry Date</label>
          <input
            type="text"
            placeholder="MM/YY"
            value={expiryDate}
            onChange={(e) => setExpiryDate(e.target.value)}
            maxLength="5"
          />
          {errors.expiryDate && <p className="error">{errors.expiryDate}</p>}

          <label>CVV</label>
          <input
            type="text"
            placeholder="123"
            value={cvv}
            onChange={(e) => setCvv(e.target.value)}
            maxLength="3"
          />
          {errors.cvv && <p className="error">{errors.cvv}</p>}

          <button type="submit" className="pay-button">Pay Now</button>
        </form>
      </div>
    </div>
  );
};

export default Checkout;
