import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom"; // Import Link for navigation
import "../styles/Categories.css";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios
      .get("https://fakestoreapi.com/products/categories")
      .then((response) => setCategories(response.data))
      .catch((error) => console.log(error));
  }, []);

  const fetchProducts = (category) => {
    setSelectedCategory(category);
    setSelectedProduct(null);
    axios
      .get(`https://fakestoreapi.com/products/category/${category}`)
      .then((response) => setProducts(response.data))
      .catch((error) => console.log(error));
  };

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingItem = prevCart.find((item) => item.id === product.id);
      if (existingItem) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prevCart, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== id));
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h2>Categories</h2>
        {categories.map((category, index) => (
          <button
            key={index}
            onClick={() => fetchProducts(category)}
            className={`category-button ${selectedCategory === category ? "active" : ""}`}
          >
            {category.toUpperCase()}
          </button>
        ))}
        <Link to="/cart" className="view-cart-button">üõí View Cart ({cart.length})</Link>
      </div>

      <div className="main-content">
        {selectedProduct ? (
          <div className="product-detail">
            <button className="back-button" onClick={() => setSelectedProduct(null)}>
              ‚Üê Back
            </button>
            <img src={selectedProduct.image} alt={selectedProduct.title} className="detail-image" />
            <h2 className="detail-title">{selectedProduct.title}</h2>
            <p className="detail-price">${selectedProduct.price}</p>
            <p className="detail-description">{selectedProduct.description}</p>
            <button className="add-to-cart" onClick={() => addToCart(selectedProduct)}>
              üõí Add to Cart
            </button>
          </div>
        ) : (
          selectedCategory && (
            <div className="products-container">
              <h2 className="products-title">{selectedCategory.toUpperCase()} Products</h2>
              <div className="products-grid">
                {products.map((product) => (
                  <div key={product.id} className="product-card">
                    <img src={product.image} alt={product.title} className="product-image" />
                    <h3 className="product-title">{product.title}</h3>
                    <p className="product-price">${product.price}</p>
                    <button className="add-to-cart" onClick={() => addToCart(product)}>
                      üõí Add to Cart
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default Categories;
